import React, { useState, useEffect } from 'react';
import { 
  PlusIcon, 
  PencilIcon, 
  TrashIcon, 
  MagnifyingGlassIcon,
  UserIcon,
  AcademicCapIcon,
  UserGroupIcon,
  XMarkIcon,
  FingerPrintIcon,
  CpuChipIcon,
  DocumentArrowUpIcon,
  CreditCardIcon
} from '@heroicons/react/24/outline';
import axios from 'axios';
import toast from 'react-hot-toast';
import { useAuth } from '../contexts/AuthContext';

function Users() {
  const { user } = useAuth();
  const [users, setUsers] = useState([]);
