import React, { useState, useEffect } from 'react';
import { useQuery } from '@tanstack/react-query';
import axios from 'axios';

const Dashboard = () => {
  const [currentTime, setCurrentTime] = useState(new Date());

  // Update time every second
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  // Fetch dashboard stats
  const { data: stats, isLoading: statsLoading } = useQuery({
    queryKey: ['dashboard-stats'],
    queryFn: async () => {
      const response = await axios.get('http://localhost:5000/api/dashboard/stats', {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });
      return response.data;
    }
  });

  // Fetch recent activity
  const { data: activity, isLoading: activityLoading } = useQuery({
    queryKey: ['dashboard-activity'],
    queryFn: async () => {
      const response = await axios.get('http://localhost:5000/api/dashboard/recent-activity', {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });
      return response.data;
    }
  });

  const StatCard = ({ title, value, icon, trend, color = 'maroon' }) => (
    <div className="ldcu-stat-card group">
      <div className="flex items-center justify-between">
        <div className="flex-1">
          <p className="text-sm font-medium text-gray-600 mb-1">{title}</p>
          <p className="text-3xl font-bold" style={{ color: color === 'gold' ? 'var(--ldcu-gold)' : 'var(--ldcu-maroon)' }}>
            {statsLoading ? '...' : value}
          </p>
          {trend && (
            <p className="text-sm text-green-600 mt-1">
              ‚ÜóÔ∏è {trend}
            </p>
          )}
        </div>
        <div className={`w-16 h-16 rounded-full flex items-center justify-center text-2xl ${
          color === 'gold' 
            ? 'bg-yellow-100 text-yellow-600' 
            : 'bg-red-100'
        }`} style={{ 
          backgroundColor: color === 'gold' ? 'rgba(212, 175, 55, 0.1)' : 'rgba(125, 29, 63, 0.1)',
          color: color === 'gold' ? 'var(--ldcu-gold)' : 'var(--ldcu-maroon)'
        }}>
          {icon}
        </div>
      </div>
    </div>
  );

  const formatTime = (date) => {
    return date.toLocaleTimeString('en-US', {
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit',
      hour12: true
    });
  };

  const formatDate = (date) => {
    return date.toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  return (
    <div className="space-y-8">
      {/* Header Section */}
      <div className="ldcu-gradient rounded-xl p-8 text-white">
        <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between">
          <div>
            <h1 className="text-3xl font-bold mb-2">Good day, Administrator!</h1>
            <p className="text-lg opacity-90">Welcome to LDCU Attendance Management System</p>
            <p className="text-sm opacity-75 mt-1">Liceo de Cagayan University</p>
          </div>
          <div className="mt-6 lg:mt-0 lg:text-right">
            <div className="text-2xl font-bold">{formatTime(currentTime)}</div>
            <div className="text-sm opacity-90">{formatDate(currentTime)}</div>
            <div className="mt-2 px-4 py-2 bg-white/20 rounded-lg text-sm">
              System Status: <span className="text-green-300">üü¢ Online</span>
            </div>
          </div>
        </div>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard
          title="Total Users"
          value={stats?.totalUsers || 0}
          icon="üë•"
          trend="+12% from last month"
          color="maroon"
        />
        <StatCard
          title="Active Devices"
          value={stats?.activeDevices || 0}
          icon="üì±"
          trend="All systems operational"
          color="gold"
        />
        <StatCard
          title="Today's Attendance"
          value={stats?.todayAttendance || 0}
          icon="üìù"
          trend="+5% from yesterday"
          color="maroon"
        />
        <StatCard
          title="Active Sessions"
          value={stats?.activeSessions || 0}
          icon="üéØ"
          trend="Currently ongoing"
          color="gold"
        />
      </div>

      {/* Main Content Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Recent Activity */}
        <div className="lg:col-span-2">
          <div className="ldcu-card p-6">
            <div className="flex items-center justify-between mb-6">
              <h3 className="text-xl font-bold" style={{ color: 'var(--ldcu-maroon)' }}>
                üìä Recent Activity
              </h3>
              <button className="text-sm hover:underline" style={{ color: 'var(--ldcu-gold)' }}>
                View All
              </button>
            </div>
            
            <div className="space-y-4">
              {activityLoading ? (
                <div className="text-center py-8 text-gray-500">
                  <div className="animate-spin w-8 h-8 border-2 border-gray-300 border-t-red-600 rounded-full mx-auto mb-2"></div>
                  Loading activity...
                </div>
              ) : activity && activity.length > 0 ? (
                activity.map((item, index) => (
                  <div key={item.id || index} className="flex items-start space-x-4 p-4 bg-gray-50 rounded-lg ldcu-gold-accent">
                    <div className="w-10 h-10 rounded-full flex items-center justify-center" style={{ backgroundColor: 'var(--ldcu-maroon)' }}>
                      <span className="text-white text-sm">
                        {item.type === 'attendance' ? 'üìù' : '‚ö°'}
                      </span>
                    </div>
                    <div className="flex-1 min-w-0">
                      <p className="text-sm font-medium text-gray-900">
                        {item.message}
                      </p>
                      <p className="text-sm text-gray-500">
                        {new Date(item.timestamp).toLocaleString()}
                      </p>
                    </div>
                  </div>
                ))
              ) : (
                <div className="text-center py-8 text-gray-500">
                  <span className="text-4xl mb-4 block">üìã</span>
                  <p>No recent activity to display</p>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Quick Actions & System Info */}
        <div className="space-y-6">
          {/* Quick Actions */}
          <div className="ldcu-card p-6">
            <h3 className="text-lg font-bold mb-4" style={{ color: 'var(--ldcu-maroon)' }}>
              ‚ö° Quick Actions
            </h3>
            <div className="space-y-3">
              <button className="ldcu-btn-primary w-full text-left flex items-center space-x-3">
                <span>üë•</span>
                <span>Manage Users</span>
              </button>
              <button className="ldcu-btn-secondary w-full text-left flex items-center space-x-3">
                <span>üì±</span>
                <span>Device Status</span>
              </button>
              <button className="ldcu-btn-primary w-full text-left flex items-center space-x-3">
                <span>üìä</span>
                <span>Generate Report</span>
              </button>
              <button className="ldcu-btn-secondary w-full text-left flex items-center space-x-3">
                <span>‚öôÔ∏è</span>
                <span>System Settings</span>
              </button>
            </div>
          </div>

          {/* System Information */}
          <div className="ldcu-card p-6">
            <h3 className="text-lg font-bold mb-4" style={{ color: 'var(--ldcu-maroon)' }}>
              üèõÔ∏è System Information
            </h3>
            <div className="space-y-3 text-sm">
              <div className="flex justify-between">
                <span className="text-gray-600">University:</span>
                <span className="font-medium">LDCU</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Academic Year:</span>
                <span className="font-medium">2024-2025</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Semester:</span>
                <span className="font-medium">First Semester</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Server Status:</span>
                <span className="ldcu-badge ldcu-badge-success">Online</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Database:</span>
                <span className="ldcu-badge ldcu-badge-success">Connected</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Last Backup:</span>
                <span className="text-gray-500">2 hours ago</span>
              </div>
            </div>
          </div>

          {/* University Motto */}
          <div className="ldcu-gradient rounded-lg p-6 text-white text-center">
            <div className="text-2xl mb-2">üèõÔ∏è</div>
            <p className="text-sm font-medium italic">
              "Nil Sine Numine"
            </p>
            <p className="text-xs opacity-80 mt-1">
              Nothing Without Divine Providence
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
