import React, { useState, useRef } from 'react';
import {
  CloudArrowUpIcon,
  DocumentIcon,
  ExclamationTriangleIcon,
  CheckCircleIcon,
  XMarkIcon,
  EyeIcon,
  ArrowDownTrayIcon,
  ClockIcon,
  UsersIcon,
  BuildingOfficeIcon,
  BookOpenIcon,
  CalendarDaysIcon,
  AcademicCapIcon
} from '@heroicons/react/24/outline';
import toast from 'react-hot-toast';
import axios from 'axios';

// Set up axios defaults
axios.defaults.baseURL = 'http://localhost:5000';

function PDFImport() {
  const [file, setFile] = useState(null);
  const [dragActive, setDragActive] = useState(false);
  const [isUploading, setIsUploading] = useState(false);
  const [isImporting, setIsImporting] = useState(false);
  const [parsedData, setParsedData] = useState(null);
  const [importResults, setImportResults] = useState(null);
  const [showPreview, setShowPreview] = useState(false);
  const [importOptions, setImportOptions] = useState({
    updateExisting: false,
    createMissingRooms: true,
    skipDuplicates: true
  });
  
  const fileInputRef = useRef(null);

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setDragActive(true);
    } else if (e.type === 'dragleave') {
      setDragActive(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      const droppedFile = e.dataTransfer.files[0];
      if (droppedFile.type === 'application/pdf') {
        setFile(droppedFile);
      } else {
        toast.error('Please upload a PDF file');
      }
    }
  };

  const handleFileSelect = (e) => {
    if (e.target.files && e.target.files[0]) {
      setFile(e.target.files[0]);
    }
  };

  const handleUploadAndPreview = async () => {
    if (!file) {
      toast.error('Please select a PDF file');
      return;
    }

    setIsUploading(true);
    setParsedData(null);
    setImportResults(null);

    try {
      const formData = new FormData();
      formData.append('pdf', file);

      const response = await axios.post('/api/import/preview', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });

      setParsedData(response.data.data);
      setShowPreview(true);
      toast.success('PDF parsed successfully! Review the data before importing.');

    } catch (error) {
      console.error('Upload error:', error);
      if (error.response?.data?.message) {
        toast.error(error.response.data.message);
      } else {
        toast.error('Failed to parse PDF');
      }
    } finally {
      setIsUploading(false);
    }
  };

  const handleImport = async () => {
    if (!parsedData) {
      toast.error('No data to import');
      return;
    }

    setIsImporting(true);

    try {
      const response = await axios.post('/api/import/execute', {
        parsedData,
        options: importOptions
      }, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });

      setImportResults(response.data.results);
      toast.success('Data imported successfully!');
      
      // Reset form
      setFile(null);
      setParsedData(null);
      setShowPreview(false);
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }

    } catch (error) {
      console.error('Import error:', error);
      if (error.response?.data?.message) {
        toast.error(error.response.data.message);
      } else {
        toast.error('Failed to import data');
      }
    } finally {
      setIsImporting(false);
    }
  };

  const resetForm = () => {
    setFile(null);
    setParsedData(null);
    setImportResults(null);
    setShowPreview(false);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const renderStatistics = (data) => {
    if (!data?.statistics) return null;

    const stats = [
      { label: 'Subjects', value: data.statistics.total_subjects, icon: BookOpenIcon, color: 'text-blue-600' },
      { label: 'Students', value: data.statistics.total_students, icon: UsersIcon, color: 'text-green-600' },
      { label: 'Instructors', value: data.statistics.total_instructors, icon: AcademicCapIcon, color: 'text-purple-600' },
      { label: 'Rooms', value: data.statistics.total_rooms, icon: BuildingOfficeIcon, color: 'text-orange-600' },
      { label: 'Schedules', value: data.statistics.total_schedules, icon: CalendarDaysIcon, color: 'text-indigo-600' },
      { label: 'Enrollments', value: data.statistics.total_enrollments, icon: ClockIcon, color: 'text-pink-600' }
    ];

    return (
      <div className="grid grid-cols-2 md:grid-cols-3 gap-4 mb-6">
        {stats.map((stat, index) => (
          <div key={index} className="bg-white rounded-lg border border-gray-200 p-4">
            <div className="flex items-center">
              <stat.icon className={`h-8 w-8 ${stat.color} mr-3`} />
              <div>
                <div className="text-2xl font-bold text-gray-900">{stat.value}</div>
                <div className="text-sm text-gray-600">{stat.label}</div>
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  };

  const renderPreviewData = () => {
    if (!parsedData) return null;

    return (
      <div className="space-y-6">
        <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
          <div className="flex items-center mb-2">
            <DocumentIcon className="h-5 w-5 text-blue-600 mr-2" />
            <h3 className="text-lg font-semibold text-blue-900">Document Information</h3>
          </div>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
            <div>
              <span className="font-medium">Institution:</span>
              <div>{parsedData.metadata.institution}</div>
            </div>
            <div>
              <span className="font-medium">Academic Year:</span>
              <div>{parsedData.metadata.academic_year}</div>
            </div>
            <div>
              <span className="font-medium">Semester:</span>
              <div>{parsedData.metadata.semester}</div>
            </div>
            <div>
              <span className="font-medium">Import Date:</span>
              <div>{new Date(parsedData.metadata.import_date).toLocaleDateString()}</div>
            </div>
          </div>
        </div>

        {renderStatistics(parsedData)}

        {/* Sample Data Preview */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Subjects Preview */}
          <div className="bg-white rounded-lg border border-gray-200 p-4">
            <h4 className="text-lg font-semibold text-gray-900 mb-3">Subjects Preview</h4>
            <div className="space-y-2 max-h-64 overflow-y-auto">
              {parsedData.subjects.slice(0, 5).map((subject, index) => (
                <div key={index} className="bg-gray-50 rounded p-3 text-sm">
                  <div className="font-medium text-gray-900">{subject.code}</div>
                  <div className="text-gray-600">{subject.name}</div>
                  <div className="text-gray-500">{subject.section}</div>
                </div>
              ))}
              {parsedData.subjects.length > 5 && (
                <div className="text-sm text-gray-500 text-center py-2">
                  And {parsedData.subjects.length - 5} more subjects...
                </div>
              )}
            </div>
          </div>

          {/* Students Preview */}
          <div className="bg-white rounded-lg border border-gray-200 p-4">
            <h4 className="text-lg font-semibold text-gray-900 mb-3">Students Preview</h4>
            <div className="space-y-2 max-h-64 overflow-y-auto">
              {parsedData.students.slice(0, 5).map((student, index) => (
                <div key={index} className="bg-gray-50 rounded p-3 text-sm">
                  <div className="font-medium text-gray-900">{student.full_name}</div>
                  <div className="text-gray-600">{student.student_id}</div>
                  <div className="text-gray-500">{student.course} - Year {student.year_level}</div>
                </div>
              ))}
              {parsedData.students.length > 5 && (
                <div className="text-sm text-gray-500 text-center py-2">
                  And {parsedData.students.length - 5} more students...
                </div>
              )}
            </div>
          </div>

          {/* Instructors Preview */}
          <div className="bg-white rounded-lg border border-gray-200 p-4">
            <h4 className="text-lg font-semibold text-gray-900 mb-3">Instructors Preview</h4>
            <div className="space-y-2 max-h-64 overflow-y-auto">
              {parsedData.instructors.slice(0, 5).map((instructor, index) => (
                <div key={index} className="bg-gray-50 rounded p-3 text-sm">
                  <div className="font-medium text-gray-900">{instructor.full_name}</div>
                  <div className="text-gray-600">{instructor.email}</div>
                  <div className="text-gray-500">{instructor.department}</div>
                </div>
              ))}
              {parsedData.instructors.length > 5 && (
                <div className="text-sm text-gray-500 text-center py-2">
                  And {parsedData.instructors.length - 5} more instructors...
                </div>
              )}
            </div>
          </div>

          {/* Rooms Preview */}
          <div className="bg-white rounded-lg border border-gray-200 p-4">
            <h4 className="text-lg font-semibold text-gray-900 mb-3">Rooms Preview</h4>
            <div className="space-y-2 max-h-64 overflow-y-auto">
              {parsedData.rooms.slice(0, 5).map((room, index) => (
                <div key={index} className="bg-gray-50 rounded p-3 text-sm">
                  <div className="font-medium text-gray-900">{room.room_number}</div>
                  <div className="text-gray-600">{room.room_name}</div>
                  <div className="text-gray-500">{room.building} - Capacity: {room.capacity}</div>
                </div>
              ))}
              {parsedData.rooms.length > 5 && (
                <div className="text-sm text-gray-500 text-center py-2">
                  And {parsedData.rooms.length - 5} more rooms...
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    );
  };

  const renderImportResults = () => {
    if (!importResults) return null;

    const totalCreated = Object.values(importResults).reduce((sum, result) => sum + (result.created || 0), 0);
    const totalUpdated = Object.values(importResults).reduce((sum, result) => sum + (result.updated || 0), 0);
    const totalErrors = Object.values(importResults).reduce((sum, result) => sum + (result.errors?.length || 0), 0);

    return (
      <div className="bg-green-50 border border-green-200 rounded-lg p-6">
        <div className="flex items-center mb-4">
          <CheckCircleIcon className="h-6 w-6 text-green-600 mr-2" />
          <h3 className="text-lg font-semibold text-green-900">Import Completed Successfully</h3>
        </div>

        <div className="grid grid-cols-3 gap-4 mb-4">
          <div className="text-center">
            <div className="text-2xl font-bold text-green-600">{totalCreated}</div>
            <div className="text-sm text-green-700">Created</div>
          </div>
          <div className="text-center">
            <div className="text-2xl font-bold text-blue-600">{totalUpdated}</div>
            <div className="text-sm text-blue-700">Updated</div>
          </div>
          <div className="text-center">
            <div className="text-2xl font-bold text-red-600">{totalErrors}</div>
            <div className="text-sm text-red-700">Errors</div>
          </div>
        </div>

        <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
          {Object.entries(importResults).map(([key, result]) => (
            <div key={key} className="bg-white rounded p-3 text-sm">
              <div className="font-medium text-gray-900 capitalize">{key}</div>
              <div className="text-green-600">Created: {result.created}</div>
              <div className="text-blue-600">Updated: {result.updated}</div>
              {result.errors?.length > 0 && (
                <div className="text-red-600">Errors: {result.errors.length}</div>
              )}
            </div>
          ))}
        </div>

        {totalErrors > 0 && (
          <div className="mt-4">
            <h4 className="font-medium text-red-900 mb-2">Import Errors:</h4>
            <div className="bg-red-50 rounded p-3 max-h-32 overflow-y-auto">
              {Object.entries(importResults).map(([key, result]) => 
                result.errors?.map((error, index) => (
                  <div key={`${key}-${index}`} className="text-sm text-red-700">
                    {error}
                  </div>
                ))
              )}
            </div>
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-2xl font-bold text-gray-900">PDF Import</h1>
        <p className="text-gray-600">Import student class lists from PDF documents</p>
      </div>

      {/* Upload Section */}
      <div className="bg-white rounded-lg shadow border border-gray-200 p-6">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">Upload Class List PDF</h2>
        
        {!file && (
          <div
            className={`relative border-2 border-dashed rounded-lg p-8 text-center transition-colors ${
              dragActive 
                ? 'border-blue-400 bg-blue-50' 
                : 'border-gray-300 hover:border-gray-400'
            }`}
            onDragEnter={handleDrag}
            onDragLeave={handleDrag}
            onDragOver={handleDrag}
            onDrop={handleDrop}
          >
            <CloudArrowUpIcon className="mx-auto h-12 w-12 text-gray-400" />
            <div className="mt-4">
              <label htmlFor="file-upload" className="cursor-pointer">
                <span className="text-base font-medium text-blue-600 hover:text-blue-500">
                  Click to upload
                </span>
                <span className="text-base text-gray-600"> or drag and drop</span>
                <input
                  id="file-upload"
                  ref={fileInputRef}
                  type="file"
                  accept=".pdf"
                  onChange={handleFileSelect}
                  className="sr-only"
                />
              </label>
            </div>
            <p className="text-sm text-gray-500">PDF files only, up to 10MB</p>
          </div>
        )}

        {file && (
          <div className="bg-gray-50 rounded-lg p-4 mb-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center">
                <DocumentIcon className="h-8 w-8 text-red-600 mr-3" />
                <div>
                  <div className="font-medium text-gray-900">{file.name}</div>
                  <div className="text-sm text-gray-500">
                    {(file.size / 1024 / 1024).toFixed(2)} MB
                  </div>
                </div>
              </div>
              <button
                onClick={resetForm}
                className="text-gray-400 hover:text-gray-600"
              >
                <XMarkIcon className="h-5 w-5" />
              </button>
            </div>
          </div>
        )}

        {file && !parsedData && (
          <div className="flex justify-end">
            <button
              onClick={handleUploadAndPreview}
              disabled={isUploading}
              className="inline-flex items-center px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 disabled:opacity-50"
            >
              {isUploading ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Parsing PDF...
                </>
              ) : (
                <>
                  <EyeIcon className="h-4 w-4 mr-2" />
                  Parse & Preview
                </>
              )}
            </button>
          </div>
        )}
      </div>

      {/* Import Options */}
      {parsedData && !importResults && (
        <div className="bg-white rounded-lg shadow border border-gray-200 p-6">
          <h2 className="text-lg font-semibold text-gray-900 mb-4">Import Options</h2>
          <div className="space-y-3">
            <label className="flex items-center">
              <input
                type="checkbox"
                checked={importOptions.updateExisting}
                onChange={(e) => setImportOptions({...importOptions, updateExisting: e.target.checked})}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="ml-2 text-sm text-gray-700">
                Update existing records (subjects, students, instructors, rooms)
              </span>
            </label>
            <label className="flex items-center">
              <input
                type="checkbox"
                checked={importOptions.createMissingRooms}
                onChange={(e) => setImportOptions({...importOptions, createMissingRooms: e.target.checked})}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="ml-2 text-sm text-gray-700">
                Create missing rooms automatically
              </span>
            </label>
            <label className="flex items-center">
              <input
                type="checkbox"
                checked={importOptions.skipDuplicates}
                onChange={(e) => setImportOptions({...importOptions, skipDuplicates: e.target.checked})}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="ml-2 text-sm text-gray-700">
                Skip duplicate enrollments and schedules
              </span>
            </label>
          </div>
        </div>
      )}

      {/* Preview Data */}
      {showPreview && parsedData && !importResults && (
        <div className="bg-white rounded-lg shadow border border-gray-200 p-6">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-lg font-semibold text-gray-900">Data Preview</h2>
            <div className="flex space-x-3">
              <button
                onClick={() => setShowPreview(false)}
                className="inline-flex items-center px-3 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 bg-white hover:bg-gray-50"
              >
                Hide Preview
              </button>
              <button
                onClick={handleImport}
                disabled={isImporting}
                className="inline-flex items-center px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-green-600 hover:bg-green-700 disabled:opacity-50"
              >
                {isImporting ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                    Importing...
                  </>
                ) : (
                  <>
                    <ArrowDownTrayIcon className="h-4 w-4 mr-2" />
                    Import Data
                  </>
                )}
              </button>
            </div>
          </div>

          {renderPreviewData()}
        </div>
      )}

      {/* Import Results */}
      {importResults && (
        <div>
          {renderImportResults()}
          <div className="mt-4 flex justify-end">
            <button
              onClick={resetForm}
              className="inline-flex items-center px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 bg-white hover:bg-gray-50"
            >
              Import Another File
            </button>
          </div>
        </div>
      )}

      {/* Instructions */}
      <div className="bg-blue-50 border border-blue-200 rounded-lg p-6">
        <h3 className="text-lg font-semibold text-blue-900 mb-3">PDF Format Requirements</h3>
        <div className="text-sm text-blue-800 space-y-2">
          <p>The PDF should contain class lists in the following format:</p>
          <ul className="list-disc list-inside space-y-1 ml-4">
            <li>Header: "LICEO DE CAGAYAN UNIVERSITY"</li>
            <li>Document type: "FINAL AND OFFICIAL LIST OF STUDENTS"</li>
            <li>Term information: "First Term, SY 2025 - 2026"</li>
            <li>Subject Code, Time, Room, Type, Descriptive Title, Section, Faculty</li>
            <li>Student list with: Count, ID Number, Name, Gender, Course, Year</li>
          </ul>
          <p className="mt-3">
            <strong>Note:</strong> The system will automatically create users, rooms, subjects, schedules, and enrollments based on the PDF data.
          </p>
        </div>
      </div>
    </div>
  );
}

export default PDFImport;
