                        {uploadResults.insertedUsers.slice(0, 5).map((user, index) => (
                          <div key={index} className="flex justify-between">
                            <span>{user.name}</span>
                            <span className="text-gray-500">{user.subject} - Year {user.year}</span>
                          </div>
