import React, { useState, useEffect } from 'react';
import { PlusIcon, PencilIcon, TrashIcon, BookOpenIcon, UserGroupIcon } from '@heroicons/react/24/outline';
import axios from 'axios';
import toast from 'react-hot-toast';

function Courses() {
  const [courses, setCourses] = useState([]);
  const [instructors, setInstructors] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [editingCourse, setEditingCourse] = useState(null);

  const [formData, setFormData] = useState({
    COURSECODE: '',
    COURSENAME: '',
    DESCRIPTION: '',
    INSTRUCTORID: '',
    SEMESTER: 'First Semester',
    YEAR: new Date().getFullYear(),
    ACADEMICYEAR: '2024-2025'
  });

  const semesterOptions = ['First Semester', 'Second Semester', 'Summer'];

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      
      // Fetch courses and instructors in parallel
      const [coursesResponse, instructorsResponse] = await Promise.all([
        axios.get('http://localhost:5000/api/courses', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        }),
        axios.get('http://localhost:5000/api/courses/instructors/list', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        })
      ]);

      setCourses(coursesResponse.data.courses || []);
      setInstructors(instructorsResponse.data || []);
      
    } catch (error) {
      console.error('Error fetching data:', error);
      if (error.response?.status === 401) {
        toast.error('Session expired. Please login again.');
        // Redirect to login or refresh token
      } else {
        toast.error('Failed to load courses');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      
      if (editingCourse) {
        // Update existing course
        const response = await axios.put(`/api/courses/${editingCourse.COURSEID}`, formData, {
          headers: { Authorization: `Bearer ${token}` }
        });
        
        // Update the courses list with the updated course
        setCourses(courses.map(c => 
          c.COURSEID === editingCourse.COURSEID ? response.data.course : c
        ));
        
        toast.success('Course updated successfully');
      } else {
        // Create new course
        const response = await axios.post('/api/courses', formData, {
          headers: { Authorization: `Bearer ${token}` }
        });
        
        // Add the new course to the list
        setCourses([...courses, response.data.course]);
        
        toast.success('Course created successfully');
      }
      
      setShowModal(false);
      setEditingCourse(null);
      resetForm();
      
    } catch (error) {
      console.error('Error saving course:', error);
      
      if (error.response?.status === 401) {
        toast.error('Session expired. Please login again.');
      } else if (error.response?.status === 409) {
        toast.error('Course already exists for this semester and academic year');
      } else if (error.response?.data?.errors) {
        // Handle validation errors
        const errorMessages = error.response.data.errors.map(err => err.msg).join(', ');
        toast.error(`Validation error: ${errorMessages}`);
      } else {
        toast.error('Failed to save course');
      }
    }
  };

  const resetForm = () => {
    setFormData({
      COURSECODE: '',
      COURSENAME: '',
      DESCRIPTION: '',
      INSTRUCTORID: '',
      SEMESTER: 'First Semester',
      YEAR: new Date().getFullYear(),
      ACADEMICYEAR: '2024-2025'
    });
  };

  const handleEdit = (course) => {
    setEditingCourse(course);
    setFormData({
      COURSECODE: course.COURSECODE,
      COURSENAME: course.COURSENAME,
      DESCRIPTION: course.DESCRIPTION || '',
      INSTRUCTORID: course.INSTRUCTORID,
      SEMESTER: course.SEMESTER,
      YEAR: course.YEAR,
      ACADEMICYEAR: course.ACADEMICYEAR
    });
    setShowModal(true);
  };

  const handleDelete = async (courseId) => {
    if (window.confirm('Are you sure you want to delete this course? This action cannot be undone.')) {
      try {
        const token = localStorage.getItem('token');
        
        await axios.delete(`/api/courses/${courseId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        
        // Remove the course from the list
        setCourses(courses.filter(c => c.COURSEID !== courseId));
        
        toast.success('Course deleted successfully');
        
      } catch (error) {
        console.error('Error deleting course:', error);
        
        if (error.response?.status === 401) {
          toast.error('Session expired. Please login again.');
        } else if (error.response?.status === 400) {
          toast.error(error.response.data.message || 'Cannot delete course');
        } else {
          toast.error('Failed to delete course');
        }
      }
    }
  };

  const getEnrollmentPercentage = (enrolled, capacity = 30) => {
    return ((enrolled / capacity) * 100).toFixed(1);
  };

  const getEnrollmentStatus = (enrolled, capacity = 30) => {
    const percentage = (enrolled / capacity) * 100;
    if (percentage >= 90) return { color: 'text-red-600', bg: 'bg-red-100', text: 'Almost Full' };
    if (percentage >= 70) return { color: 'text-yellow-600', bg: 'bg-yellow-100', text: 'Filling Up' };
    return { color: 'text-green-600', bg: 'bg-green-100', text: 'Open' };
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Course Management</h1>
          <p className="text-gray-600">Manage courses, enrollments, and academic programs</p>
        </div>
        <button
          onClick={() => setShowModal(true)}
          className="inline-flex items-center px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700"
        >
          <PlusIcon className="h-4 w-4 mr-2" />
          Add Course
        </button>
      </div>

      {/* Statistics */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center">
            <BookOpenIcon className="h-8 w-8 text-blue-600" />
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Total Courses</p>
              <p className="text-2xl font-semibold text-gray-900">{courses.length}</p>
            </div>
          </div>
        </div>
        
        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center">
            <UserGroupIcon className="h-8 w-8 text-green-600" />
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Total Enrollments</p>
              <p className="text-2xl font-semibold text-gray-900">
                {courses.reduce((sum, course) => sum + (course.enrolled_students || 0), 0)}
              </p>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center">
            <div className="h-8 w-8 bg-yellow-100 rounded-full flex items-center justify-center">
              <span className="text-yellow-600 font-semibold">%</span>
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Avg. Enrollment</p>
              <p className="text-2xl font-semibold text-gray-900">
                {courses.length > 0 ? (
                  (courses.reduce((sum, course) => sum + ((course.enrolled_students || 0) / 30), 0) / courses.length * 100).toFixed(1)
                ) : 0}%
              </p>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center">
            <div className="h-8 w-8 bg-purple-100 rounded-full flex items-center justify-center">
              <span className="text-purple-600 font-semibold">#</span>
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-500">Instructors</p>
              <p className="text-2xl font-semibold text-gray-900">
                {new Set(courses.map(c => c.INSTRUCTORID)).size}
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Courses Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {courses.map((course) => {
          const enrollmentStatus = getEnrollmentStatus(course.enrolled_students || 0);
          return (
            <div key={course.COURSEID} className="bg-white rounded-lg shadow border border-gray-200 p-6">
              <div className="flex justify-between items-start mb-4">
                <div>
                  <h3 className="text-lg font-semibold text-gray-900">{course.COURSECODE}</h3>
                  <p className="text-sm text-gray-600">{course.COURSENAME}</p>
                </div>
                <div className="flex space-x-2">
                  <button
                    onClick={() => handleEdit(course)}
                    className="text-gray-400 hover:text-blue-600"
                  >
                    <PencilIcon className="h-4 w-4" />
                  </button>
                  <button
                    onClick={() => handleDelete(course.COURSEID)}
                    className="text-gray-400 hover:text-red-600"
                  >
                    <TrashIcon className="h-4 w-4" />
                  </button>
                </div>
              </div>

              <p className="text-sm text-gray-600 mb-4 line-clamp-3">{course.DESCRIPTION || 'No description available'}</p>

              <div className="space-y-2 mb-4">
                <div className="flex justify-between text-sm">
                  <span className="text-gray-500">Instructor:</span>
                  <span className="font-medium">{course.instructor_name || 'TBA'}</span>
                </div>
                <div className="flex justify-between text-sm">
                  <span className="text-gray-500">Year:</span>
                  <span className="font-medium">{course.YEAR}</span>
                </div>
                <div className="flex justify-between text-sm">
                  <span className="text-gray-500">Semester:</span>
                  <span className="font-medium">{course.SEMESTER}</span>
                </div>
                <div className="flex justify-between text-sm">
                  <span className="text-gray-500">Academic Year:</span>
                  <span className="font-medium">{course.ACADEMICYEAR}</span>
                </div>
              </div>

              {/* Enrollment Progress */}
              <div className="mb-4">
                <div className="flex justify-between items-center mb-2">
                  <span className="text-sm font-medium text-gray-700">Enrollment</span>
                  <span className={`text-xs px-2 py-1 rounded-full ${enrollmentStatus.bg} ${enrollmentStatus.color}`}>
                    {enrollmentStatus.text}
                  </span>
                </div>
                <div className="w-full bg-gray-200 rounded-full h-2">
                  <div 
                    className="bg-blue-600 h-2 rounded-full" 
                    style={{ width: `${getEnrollmentPercentage(course.enrolled_students || 0)}%` }}
                  ></div>
                </div>
                <div className="flex justify-between text-xs text-gray-500 mt-1">
                  <span>{course.enrolled_students || 0} enrolled</span>
                  <span>30 capacity</span>
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {courses.length === 0 && (
        <div className="text-center py-12">
          <BookOpenIcon className="mx-auto h-12 w-12 text-gray-400" />
          <h3 className="mt-2 text-sm font-medium text-gray-900">No courses</h3>
          <p className="mt-1 text-sm text-gray-500">Get started by creating a new course.</p>
        </div>
      )}

      {/* Create/Edit Course Modal */}
      {showModal && (
        <div className="fixed inset-0 z-50 overflow-y-auto">
          <div className="flex items-center justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
            <div className="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity"></div>
            
            <div className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
              <form onSubmit={handleSubmit}>
                <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                  <h3 className="text-lg font-medium text-gray-900 mb-4">
                    {editingCourse ? 'Edit Course' : 'Create New Course'}
                  </h3>
                  
                  <div className="grid grid-cols-1 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700">Course Code</label>
                      <input
                        type="text"
                        value={formData.COURSECODE}
                        onChange={(e) => setFormData({...formData, COURSECODE: e.target.value})}
                        className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                        placeholder="e.g., CS101"
                        required
                      />
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700">Course Name</label>
                      <input
                        type="text"
                        value={formData.COURSENAME}
                        onChange={(e) => setFormData({...formData, COURSENAME: e.target.value})}
                        className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                        placeholder="e.g., Introduction to Computer Science"
                        required
                      />
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700">Description</label>
                      <textarea
                        value={formData.DESCRIPTION}
                        onChange={(e) => setFormData({...formData, DESCRIPTION: e.target.value})}
                        className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                        rows="3"
                        placeholder="Course description..."
                      />
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700">Instructor</label>
                      <select
                        value={formData.INSTRUCTORID}
                        onChange={(e) => setFormData({...formData, INSTRUCTORID: e.target.value})}
                        className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                        required
                      >
                        <option value="">Select Instructor</option>
                        {instructors.map(instructor => (
                          <option key={instructor.USERID} value={instructor.USERID}>
                            {instructor.FIRSTNAME} {instructor.LASTNAME} ({instructor.EMPLOYEEID})
                          </option>
                        ))}
                      </select>
                    </div>

                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <label className="block text-sm font-medium text-gray-700">Semester</label>
                        <select
                          value={formData.SEMESTER}
                          onChange={(e) => setFormData({...formData, SEMESTER: e.target.value})}
                          className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                        >
                          {semesterOptions.map(semester => (
                            <option key={semester} value={semester}>{semester}</option>
                          ))}
                        </select>
                      </div>

                      <div>
                        <label className="block text-sm font-medium text-gray-700">Year</label>
                        <input
                          type="number"
                          value={formData.YEAR}
                          onChange={(e) => setFormData({...formData, YEAR: e.target.value})}
                          className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                          min="2020"
                          max="2030"
                          required
                        />
                      </div>
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700">Academic Year</label>
                      <input
                        type="text"
                        value={formData.ACADEMICYEAR}
                        onChange={(e) => setFormData({...formData, ACADEMICYEAR: e.target.value})}
                        className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                        placeholder="e.g., 2024-2025"
                        pattern="^\d{4}-\d{4}$"
                        title="Format: YYYY-YYYY"
                        required
                      />
                    </div>
                  </div>
                </div>

                <div className="bg-gray-50 px-4 py-3 sm:px-6 sm:flex sm:flex-row-reverse">
                  <button
                    type="submit"
                    className="w-full inline-flex justify-center rounded-md border border-transparent shadow-sm px-4 py-2 bg-blue-600 text-base font-medium text-white hover:bg-blue-700 focus:outline-none sm:ml-3 sm:w-auto sm:text-sm"
                  >
                    {editingCourse ? 'Update' : 'Create'}
                  </button>
                  <button
                    type="button"
                    onClick={() => {
                      setShowModal(false);
                      setEditingCourse(null);
                      resetForm();
                    }}
                    className="mt-3 w-full inline-flex justify-center rounded-md border border-gray-300 shadow-sm px-4 py-2 bg-white text-base font-medium text-gray-700 hover:bg-gray-50 focus:outline-none sm:mt-0 sm:ml-3 sm:w-auto sm:text-sm"
                  >
                    Cancel
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Courses;
