import React, { useState, useEffect } from 'react';
import { 
  PlusIcon, 
  PencilIcon, 
  TrashIcon, 
  MagnifyingGlassIcon,
  UserIcon,
  AcademicCapIcon,
  UserGroupIcon,
  XMarkIcon,
  FingerPrintIcon,
  CpuChipIcon,
  DocumentArrowUpIcon
} from '@heroicons/react/24/outline';
import axios from 'axios';
import toast from 'react-hot-toast';
import { useAuth } from '../contexts/AuthContext';

function Users() {
  const { user } = useAuth();
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [editingUser, setEditingUser] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterType, setFilterType] = useState('all');
  const [filterStatus, setFilterStatus] = useState('all');
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [testingAttendance, setTestingAttendance] = useState(null);
  const [showUploadModal, setShowUploadModal] = useState(false);
  const [uploadFile, setUploadFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [uploadResults, setUploadResults] = useState(null);
  const [previewData, setPreviewData] = useState(null);
  const [showPreview, setShowPreview] = useState(false);
  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    email: '',
    password: '',
    user_type: 'student',
    student_id: '',
    employee_id: '',
    year_level: '',
    department: '',
    status: 'active'
  });

  useEffect(() => {
    fetchUsers();
  }, [currentPage, filterType, filterStatus, searchTerm]);

  const fetchUsers = async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams({
        page: currentPage,
        limit: 20
      });

      if (filterType !== 'all') {
        params.append('type', filterType);
      }
      if (filterStatus !== 'all') {
        params.append('status', filterStatus);
      }
      if (searchTerm) {
        params.append('search', searchTerm);
      }

      const response = await axios.get(`http://localhost:5000/api/users?${params}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });

      setUsers(response.data.users);
      setTotalPages(response.data.pagination.pages);
    } catch (error) {
      console.error('Error fetching users:', error);
      toast.error('Failed to load users');
    } finally {
      setLoading(false);
    }
  };

  const handleTestAttendance = async (userId, authMethod = 'RFID') => {
    try {
      setTestingAttendance(userId);

      const response = await axios.post(
        `http://localhost:5000/api/users/${userId}/test-attendance`,
        { 
          authMethod: authMethod,
          location: 'inside'
        },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        }
      );

      const { attendance } = response.data;

      toast.success(
        <div>
          <div className="font-medium"> Attendance Recorded!</div>
          <div className="text-sm mt-1">
            <div><strong>{attendance.user}</strong></div>
            <div>{attendance.course} - {attendance.room}</div>
            <div>Status: <span className={`font-medium ${
              attendance.status === 'Present' ? 'text-green-600' : 
              attendance.status === 'Late' ? 'text-yellow-600' : 'text-red-600'
            }`}>{attendance.status}</span></div>
            <div>Auth: {attendance.authMethod}</div>
          </div>
        </div>,
        { duration: 4000 }
      );
    } catch (error) {
      console.error('Error testing attendance:', error);
      toast.error(error.response?.data?.message || 'Failed to record test attendance');
    } finally {
      setTestingAttendance(null);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (editingUser) {
        await axios.put(`http://localhost:5000/api/users/${editingUser.USERID}`, formData, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        toast.success('User updated successfully');
      } else {
        await axios.post('http://localhost:5000/api/users', formData, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        toast.success('User created successfully');
      }

      setShowModal(false);
      setEditingUser(null);
      resetForm();
      fetchUsers();
    } catch (error) {
      console.error('Error saving user:', error);
      toast.error(error.response?.data?.message || 'Failed to save user');
    }
  };

  const handleEdit = (user) => {
    setEditingUser(user);
    setFormData({
      first_name: user.FIRSTNAME || '',
      last_name: user.LASTNAME || '',
      email: user.EMAIL || '',
      password: '',
      user_type: user.USERTYPE || 'student',
      student_id: user.STUDENTID || '',
      employee_id: user.EMPLOYEEID || '',
      year_level: user.YEARLEVEL || '',
      department: user.DEPARTMENT || '',
      status: user.STATUS || 'active'
    });
    setShowModal(true);
  };

  const handleDelete = async (userId) => {
    if (!window.confirm('Are you sure you want to delete this user?')) {
      return;
    }

    try {
      await axios.delete(`http://localhost:5000/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      toast.success('User deleted successfully');
      fetchUsers();
    } catch (error) {
      console.error('Error deleting user:', error);
      toast.error(error.response?.data?.message || 'Failed to delete user');
    }
  };

  const resetForm = () => {
    setFormData({
      first_name: '',
      last_name: '',
      email: '',
      password: '',
      user_type: 'student',
      student_id: '',
      employee_id: '',
      year_level: '',
      department: '',
      status: 'active'
    });
  };

  const handleFilePreview = async () => {
    if (!uploadFile) {
      toast.error('Please select a file to upload');
      return;
    }

    setUploading(true);
    const formData = new FormData();
    formData.append('excelFile', uploadFile);

    try {
      const response = await axios.post('http://localhost:5000/api/users/preview-excel', formData, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'multipart/form-data'
        }
      });

      setPreviewData(response.data);
      setShowPreview(true);
      toast.success(`Preview generated for ${response.data.summary.previewRows} users`);

    } catch (error) {
      console.error('Preview error:', error);
      toast.error(error.response?.data?.message || 'Failed to preview Excel file');

      if (error.response?.data) {
        setPreviewData(error.response.data);
        setShowPreview(true);
      }
    } finally {
      setUploading(false);
    }
  };

  const handleConfirmUpload = async () => {
    if (!uploadFile) {
      toast.error('Please select a file to upload');
      return;
    }

    setUploading(true);
    const formData = new FormData();
    formData.append('excelFile', uploadFile);

    try {
      const response = await axios.post('http://localhost:5000/api/users/upload-excel', formData, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'multipart/form-data'
        }
      });

      setUploadResults(response.data);
      setShowPreview(false);
      setPreviewData(null);
      toast.success(`Successfully processed ${response.data.summary.insertedUsers} users from Excel file`);

      // Refresh the users list
      fetchUsers();

      // Reset upload state
      setUploadFile(null);

    } catch (error) {
      console.error('Upload error:', error);
      toast.error(error.response?.data?.message || 'Failed to upload Excel file');

      if (error.response?.data?.errors) {
        setUploadResults(error.response.data);
      }
    } finally {
      setUploading(false);
    }
  };

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      // Validate file type
      const allowedTypes = ['.xlsx', '.xls', '.csv'];
      const fileExtension = file.name.toLowerCase().substring(file.name.lastIndexOf('.'));

      if (!allowedTypes.includes(fileExtension)) {
        toast.error('Please select a valid Excel file (.xlsx, .xls) or CSV file');
        return;
      }

      // Validate file size (10MB limit)
      if (file.size > 10 * 1024 * 1024) {
        toast.error('File size must be less than 10MB');
        return;
      }

      setUploadFile(file);
      setUploadResults(null);
    }
  };

  const resetUploadModal = () => {
    setShowUploadModal(false);
    setUploadFile(null);
    setUploadResults(null);
    setPreviewData(null);
    setShowPreview(false);
    setUploading(false);
  };

  const getUserTypeIcon = (type) => {
    switch (type) {
      case 'admin':
        return <UserIcon className="h-5 w-5 text-red-600" />;
      case 'instructor':
        return <AcademicCapIcon className="h-5 w-5 text-blue-600" />;
      case 'student':
        return <UserGroupIcon className="h-5 w-5 text-green-600" />;
      default:
        return <UserIcon className="h-5 w-5 text-gray-600" />;
    }
  };

  const getUserTypeColor = (type) => {
    switch (type) {
      case 'admin':
        return 'bg-red-100 text-red-800';
      case 'instructor':
        return 'bg-blue-100 text-blue-800';
      case 'student':
        return 'bg-green-100 text-green-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusColor = (status) => {
    return status === 'active' 
      ? 'bg-green-100 text-green-800' 
      : 'bg-red-100 text-red-800';
  };

  if (loading && users.length === 0) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">User Management</h1>
          <p className="text-gray-600">Manage students, instructors, and administrators. Test attendance simulation available.</p>
        </div>
        {user?.role === 'admin' && (
          <div className="flex space-x-3">
            <button
              onClick={() => setShowUploadModal(true)}
              className="btn btn-secondary flex items-center space-x-2"
            >
              <DocumentArrowUpIcon className="h-5 w-5" />
              <span>Upload CSV/Excel</span>
            </button>
            <button
              onClick={() => setShowModal(true)}
              className="btn btn-primary flex items-center space-x-2"
            >
              <PlusIcon className="h-5 w-5" />
              <span>Add User</span>
            </button>
          </div>
        )}
      </div>

      {/* Filters */}
      <div className="bg-white rounded-lg shadow p-6">
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Search</label>
            <div className="relative">
              <MagnifyingGlassIcon className="h-5 w-5 absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
              <input
                type="text"
                placeholder="Search users..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-1 focus:ring-blue-500"
              />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">User Type</label>
            <select
              value={filterType}
              onChange={(e) => setFilterType(e.target.value)}
              className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-1 focus:ring-blue-500"
            >
              <option value="all">All Types</option>
              <option value="student">Students</option>
              <option value="instructor">Instructors</option>
              <option value="admin">Administrators</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Status</label>
            <select
              value={filterStatus}
              onChange={(e) => setFilterStatus(e.target.value)}
              className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-1 focus:ring-blue-500"
            >
              <option value="all">All Status</option>
              <option value="active">Active</option>
              <option value="inactive">Inactive</option>
            </select>
          </div>

          <div className="flex items-end">
            <button
              onClick={() => {
                setSearchTerm('');
                setFilterType('all');
                setFilterStatus('all');
                setCurrentPage(1);
              }}
              className="w-full px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 bg-white hover:bg-gray-50"
            >
              Clear Filters
            </button>
          </div>
        </div>
      </div>

      {/* Users List */}
      <div className="bg-white rounded-lg shadow">
        <div className="px-6 py-4 border-b border-gray-200">
          <h2 className="text-lg font-semibold text-gray-900">
            Users ({users.length})
          </h2>
        </div>

        {users.length === 0 ? (
          <div className="text-center py-12">
            <UserIcon className="mx-auto h-12 w-12 text-gray-400" />
            <h3 className="mt-2 text-sm font-medium text-gray-900">No users found</h3>
            <p className="mt-1 text-sm text-gray-500">
              {searchTerm || filterType !== 'all' || filterStatus !== 'all'
                ? 'Try adjusting your search or filter criteria.'
                : 'Get started by creating a new user.'
              }
            </p>
          </div>
        ) : (
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    User
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Type
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    ID
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Course/Major
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Status
                  </th>
                  <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Test Attendance
                  </th>
                  {user?.role === 'admin' && (
                    <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Actions
                    </th>
                  )}
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {users.map((userData) => (
                  <tr key={userData.USERID} className="hover:bg-gray-50">
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center">
                        <div className="flex-shrink-0 h-10 w-10">
                          <div className="h-10 w-10 rounded-full bg-gray-200 flex items-center justify-center">
                            {getUserTypeIcon(userData.USERTYPE)}
                          </div>
                        </div>
                        <div className="ml-4">
                          <div className="text-sm font-medium text-gray-900">
                            {userData.FIRSTNAME} {userData.LASTNAME}
                          </div>
                          <div className="text-sm text-gray-500">{userData.EMAIL}</div>
                        </div>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getUserTypeColor(userData.USERTYPE)}`}>
                        {userData.USERTYPE}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      {userData.STUDENTID || userData.EMPLOYEEID || '-'}
                      {userData.YEARLEVEL && (
                        <div className="text-xs text-gray-500">Year {userData.YEARLEVEL}</div>
                      )}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      {userData.DEPARTMENT || '-'}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getStatusColor(userData.STATUS)}`}>
                        {userData.STATUS}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-center">
                      <div className="flex items-center justify-center space-x-2">
                        <button
                          onClick={() => handleTestAttendance(userData.USERID, 'RFID')}
                          disabled={testingAttendance === userData.USERID || userData.STATUS !== 'active'}
                          className="inline-flex items-center px-2 py-1 text-xs font-medium text-blue-600 bg-blue-100 rounded-md hover:bg-blue-200 disabled:opacity-50 disabled:cursor-not-allowed transition-colors duration-200"
                          title="Simulate RFID Scan"
                        >
                          {testingAttendance === userData.USERID ? (
                            <div className="animate-spin h-3 w-3 border border-blue-600 rounded-full border-t-transparent"></div>
                          ) : (
                            <CpuChipIcon className="h-3 w-3" />
                          )}
                          <span className="ml-1">RFID</span>
                        </button>
                        <button
                          onClick={() => handleTestAttendance(userData.USERID, 'Fingerprint')}
                          disabled={testingAttendance === userData.USERID || userData.STATUS !== 'active'}
                          className="inline-flex items-center px-2 py-1 text-xs font-medium text-green-600 bg-green-100 rounded-md hover:bg-green-200 disabled:opacity-50 disabled:cursor-not-allowed transition-colors duration-200"
                          title="Simulate Fingerprint Scan"
                        >
                          {testingAttendance === userData.USERID ? (
                            <div className="animate-spin h-3 w-3 border border-green-600 rounded-full border-t-transparent"></div>
                          ) : (
                            <FingerPrintIcon className="h-3 w-3" />
                          )}
                          <span className="ml-1">Finger</span>
                        </button>
                      </div>
                      {userData.STATUS !== 'active' && (
                        <div className="text-xs text-gray-400 mt-1">
                          User inactive
                        </div>
                      )}
                    </td>
                    {user?.role === 'admin' && (
                      <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                        <div className="flex items-center justify-end space-x-2">
                          <button
                            onClick={() => handleEdit(userData)}
                            className="text-blue-600 hover:text-blue-900 transition-colors duration-200"
                          >
                            <PencilIcon className="h-4 w-4" />
                          </button>
                          <button
                            onClick={() => handleDelete(userData.USERID)}
                            className="text-red-600 hover:text-red-900 transition-colors duration-200"
                          >
                            <TrashIcon className="h-4 w-4" />
                          </button>
                        </div>
                      </td>
                    )}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {/* Pagination */}
        {totalPages > 1 && (
          <div className="px-6 py-4 border-t border-gray-200">
            <div className="flex items-center justify-between">
              <div className="text-sm text-gray-700">
                Page {currentPage} of {totalPages}
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={() => setCurrentPage(Math.max(1, currentPage - 1))}
                  disabled={currentPage === 1}
                  className="px-3 py-1 border border-gray-300 rounded-md text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  Previous
                </button>
                <button
                  onClick={() => setCurrentPage(Math.min(totalPages, currentPage + 1))}
                  disabled={currentPage === totalPages}
                  className="px-3 py-1 border border-gray-300 rounded-md text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  Next
                </button>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Add/Edit Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-semibold text-gray-900">
                {editingUser ? 'Edit User' : 'Add New User'}
              </h3>
              <button
                onClick={() => {
                  setShowModal(false);
                  setEditingUser(null);
                  resetForm();
                }}
                className="text-gray-400 hover:text-gray-600"
              >
                <XMarkIcon className="h-6 w-6" />
              </button>
            </div>

            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    First Name *
                  </label>
                  <input
                    type="text"
                    required
                    value={formData.first_name}
                    onChange={(e) => setFormData({ ...formData, first_name: e.target.value })}
                    className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-1 focus:ring-blue-500"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Last Name *
                  </label>
                  <input
                    type="text"
                    required
                    value={formData.last_name}
                    onChange={(e) => setFormData({ ...formData, last_name: e.target.value })}
                    className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-1 focus:ring-blue-500"
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Email *
                </label>
                <input
                  type="email"
                  required
                  value={formData.email}
                  onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                  className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-1 focus:ring-blue-500"
                />
              </div>

              {!editingUser && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Password *
                  </label>
                  <input
                    type="password"
                    required={!editingUser}
                    value={formData.password}
                    onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                    className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-1 focus:ring-blue-500"
                  />
                </div>
              )}

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    User Type *
                  </label>
                  <select
                    required
                    value={formData.user_type}
                    onChange={(e) => setFormData({ ...formData, user_type: e.target.value })}
                    className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-1 focus:ring-blue-500"
                  >
                    <option value="student">Student</option>
                    <option value="instructor">Instructor</option>
                    <option value="admin">Administrator</option>
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Status
                  </label>
                  <select
                    value={formData.status}
                    onChange={(e) => setFormData({ ...formData, status: e.target.value })}
                    className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-1 focus:ring-blue-500"
                  >
                    <option value="active">Active</option>
                    <option value="inactive">Inactive</option>
                  </select>
                </div>
              </div>

              {formData.user_type === 'student' && (
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Student ID
                    </label>
                    <input
                      type="text"
                      value={formData.student_id}
                      onChange={(e) => setFormData({ ...formData, student_id: e.target.value })}
                      className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-1 focus:ring-blue-500"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Year Level
                    </label>
                    <select
                      value={formData.year_level}
                      onChange={(e) => setFormData({ ...formData, year_level: e.target.value })}
                      className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-1 focus:ring-blue-500"
                    >
                      <option value="">Select Year</option>
                      <option value="1">1st Year</option>
                      <option value="2">2nd Year</option>
                      <option value="3">3rd Year</option>
                      <option value="4">4th Year</option>
                    </select>
                  </div>
                </div>
              )}

              {(formData.user_type === 'instructor' || formData.user_type === 'admin') && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Employee ID
                  </label>
                  <input
                    type="text"
                    value={formData.employee_id}
                    onChange={(e) => setFormData({ ...formData, employee_id: e.target.value })}
                    className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-1 focus:ring-blue-500"
                  />
                </div>
              )}

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Department
                </label>
                <input
                  type="text"
                  value={formData.department}
                  onChange={(e) => setFormData({ ...formData, department: e.target.value })}
                  className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-1 focus:ring-blue-500"
                />
              </div>

              <div className="flex justify-end space-x-3 pt-4">
                <button
                  type="button"
                  onClick={() => {
                    setShowModal(false);
                    setEditingUser(null);
                    resetForm();
                  }}
                  className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 bg-white hover:bg-gray-50"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700"
                >
                  {editingUser ? 'Update' : 'Create'} User
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Upload Excel/CSV Modal */}
      {showUploadModal && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-full max-w-2xl shadow-lg rounded-md bg-white">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-semibold text-gray-900">
                Upload Excel/CSV File
              </h3>
              <button
                onClick={resetUploadModal}
                className="text-gray-400 hover:text-gray-600"
              >
                <XMarkIcon className="h-6 w-6" />
              </button>
            </div>

            <div className="space-y-4">
              {/* Instructions */}
              <div className="bg-blue-50 border border-blue-200 rounded-md p-4">
                <h4 className="font-medium text-blue-800 mb-2">Instructions:</h4>
                <ul className="text-sm text-blue-700 space-y-1">
                  <li> Upload an Excel file (.xlsx, .xls) or CSV file</li>
                  <li> The file must contain a sheet named "SBO"</li>
                  <li> Required columns: "SL#", "Student Name", "Course/Major", "Year"</li>
                  <li> Student names can be: "LASTNAME, FIRSTNAME" or "LASTNAME FIRSTNAME"</li>
                  <li> System processes rows with valid SL# numbers only</li>
                  <li> Processing stops when SL# is missing or invalid</li>
                  <li> Maximum file size: 10MB</li>
                </ul>
              </div>

              {/* File Upload */}
              <div className="border-2 border-dashed border-gray-300 rounded-lg p-6">
                <div className="text-center">
                  <DocumentArrowUpIcon className="mx-auto h-12 w-12 text-gray-400" />
                  <div className="mt-4">
                    <label htmlFor="file-upload" className="cursor-pointer">
                      <span className="mt-2 block text-sm font-medium text-gray-900">
                        {uploadFile ? uploadFile.name : 'Select a file to upload'}
                      </span>
                      <input
                        id="file-upload"
                        name="file-upload"
                        type="file"
                        className="sr-only"
                        accept=".xlsx,.xls,.csv"
                        onChange={handleFileSelect}
                        disabled={uploading}
                      />
                      <span className="mt-1 block text-sm text-gray-500">
                        or drag and drop
                      </span>
                    </label>
                  </div>
                  <p className="mt-1 text-xs text-gray-500">
                    Excel (.xlsx, .xls) or CSV files up to 10MB
                  </p>
                </div>
              </div>

              {/* Preview Data */}
              {showPreview && previewData && (
                <div className="space-y-3">
                  <div className="bg-blue-50 border border-blue-200 rounded-md p-4">
                    <h4 className="font-medium text-blue-800 mb-2">Preview Summary:</h4>
                    <div className="text-sm text-blue-700 space-y-1">
                      <p>Sheet found: <strong>{previewData.sheetName}</strong></p>
                      <p>Total data rows available: {previewData.summary?.totalRows || 0}</p>
                      <p>Valid students found: {previewData.summary?.previewRows || 0}</p>
                      <p>Issues detected: {previewData.summary?.errors || 0}</p>
                    </div>
                    {previewData.headers && (
                      <div className="mt-2 text-xs text-blue-600">
                        <p>Columns detected: {previewData.headers.studentNameCol}, {previewData.headers.courseMajorCol}, {previewData.headers.yearCol}</p>
                      </div>
                    )}
                  </div>

                  {previewData.errors && previewData.errors.length > 0 && (
                    <div className="bg-red-50 border border-red-200 rounded-md p-4 max-h-32 overflow-y-auto">
                      <h4 className="font-medium text-red-800 mb-2">Issues Found:</h4>
                      <ul className="text-sm text-red-700 space-y-1">
                        {previewData.errors.slice(0, 5).map((error, index) => (
                          <li key={index}> {error}</li>
                        ))}
                        {previewData.errors.length > 5 && (
                          <li className="text-red-600 font-medium">
                            ... and {previewData.errors.length - 5} more issues
                          </li>
                        )}
                      </ul>
                    </div>
                  )}

                  {previewData.previewUsers && previewData.previewUsers.length > 0 && (
                    <div className="bg-gray-50 border border-gray-200 rounded-md p-4 max-h-64 overflow-y-auto">
                      <h4 className="font-medium text-gray-800 mb-2">Data Preview (All {previewData.previewUsers.length} students found):</h4>
                      <div className="overflow-x-auto">
                        <table className="min-w-full text-xs">
                          <thead className="bg-gray-100">
                            <tr>
                              <th className="px-2 py-1 text-left font-medium text-gray-600">Row</th>
                              <th className="px-2 py-1 text-left font-medium text-gray-600">SL#</th>
                              <th className="px-2 py-1 text-left font-medium text-gray-600">Original Name</th>
                              <th className="px-2 py-1 text-left font-medium text-gray-600">First Name</th>
                              <th className="px-2 py-1 text-left font-medium text-gray-600">Last Name</th>
                              <th className="px-2 py-1 text-left font-medium text-gray-600">Email</th>
                              <th className="px-2 py-1 text-left font-medium text-gray-600">Course/Major</th>
                              <th className="px-2 py-1 text-left font-medium text-gray-600">Year</th>
                              <th className="px-2 py-1 text-left font-medium text-gray-600">Student ID</th>
                            </tr>
                          </thead>
                          <tbody className="divide-y divide-gray-200">
                            {previewData.previewUsers.map((user, index) => (
                              <tr key={index} className="hover:bg-gray-50">
                                <td className="px-2 py-1 text-gray-500">{user.rowNumber}</td>
                                <td className="px-2 py-1 text-blue-600 font-medium">{user.slNumber}</td>
                                <td className="px-2 py-1 text-gray-900">{user.originalName}</td>
                                <td className="px-2 py-1 text-gray-900">{user.firstName}</td>
                                <td className="px-2 py-1 text-gray-900">{user.lastName}</td>
                                <td className="px-2 py-1 text-gray-600">{user.email}</td>
                                <td className="px-2 py-1 text-gray-900">{user.courseMajor}</td>
                                <td className="px-2 py-1 text-gray-900">{user.yearLevel}</td>
                                <td className="px-2 py-1 text-gray-600">{user.studentId}</td>
                              </tr>
                            ))}
                          </tbody>
                        </table>
                      </div>
                    </div>
                  )}
                </div>
              )}

              {/* Upload Results */}
              {uploadResults && (
                <div className="space-y-3">
                  <div className="bg-green-50 border border-green-200 rounded-md p-4">
                    <h4 className="font-medium text-green-800 mb-2">Upload Complete!</h4>
                    <div className="text-sm text-green-700 space-y-1">
                      <p>Total rows processed: {uploadResults.summary?.totalRows || 0}</p>
                      <p>Users successfully imported: {uploadResults.summary?.insertedUsers || 0}</p>
                      <p>Errors encountered: {uploadResults.summary?.errors || 0}</p>
                    </div>
                  </div>

                  {uploadResults.errors && uploadResults.errors.length > 0 && (
                    <div className="bg-red-50 border border-red-200 rounded-md p-4 max-h-40 overflow-y-auto">
                      <h4 className="font-medium text-red-800 mb-2">Errors:</h4>
                      <ul className="text-sm text-red-700 space-y-1">
                        {uploadResults.errors.slice(0, 10).map((error, index) => (
                          <li key={index}> {error}</li>
                        ))}
                        {uploadResults.errors.length > 10 && (
                          <li className="text-red-600 font-medium">
                            ... and {uploadResults.errors.length - 10} more errors
                          </li>
                        )}
                      </ul>
                    </div>
                  )}

                  {uploadResults.insertedUsers && uploadResults.insertedUsers.length > 0 && (
                    <div className="bg-gray-50 border border-gray-200 rounded-md p-4 max-h-40 overflow-y-auto">
                      <h4 className="font-medium text-gray-800 mb-2">Successfully Imported Users:</h4>
                      <div className="text-sm text-gray-700 space-y-1">
                        {uploadResults.insertedUsers.slice(0, 5).map((user, index) => (
                          <div key={index} className="flex justify-between">
                            <span>{user.name}</span>
                            <span className="text-gray-500">{user.course} - Year {user.year}</span>
                          </div>
                        ))}
                        {uploadResults.insertedUsers.length > 5 && (
                          <p className="text-gray-600 font-medium">
                            ... and {uploadResults.insertedUsers.length - 5} more users
                          </p>
                        )}
                      </div>
                    </div>
                  )}
                </div>
              )}

              {/* Action Buttons */}
              <div className="flex justify-end space-x-3 pt-4">
                <button
                  type="button"
                  onClick={resetUploadModal}
                  className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 bg-white hover:bg-gray-50"
                  disabled={uploading}
                >
                  {uploadResults ? 'Close' : 'Cancel'}
                </button>

                {!showPreview && !uploadResults && (
                  <button
                    onClick={handleFilePreview}
                    disabled={!uploadFile || uploading}
                    className="px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    {uploading ? (
                      <div className="flex items-center">
                        <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                        Processing...
                      </div>
                    ) : (
                      'Preview Data'
                    )}
                  </button>
                )}

                {showPreview && !uploadResults && (
                  <>
                    <button
                      onClick={() => {
                        setShowPreview(false);
                        setPreviewData(null);
                      }}
                      className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 bg-white hover:bg-gray-50"
                      disabled={uploading}
                    >
                      Back to File Selection
                    </button>
                    <button
                      onClick={handleConfirmUpload}
                      disabled={uploading}
                      className="px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-green-600 hover:bg-green-700 disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                      {uploading ? (
                        <div className="flex items-center">
                          <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                          Uploading...
                        </div>
                      ) : (
                        'Confirm Upload'
                      )}
                    </button>
                  </>
                )}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Users;
