import React, { useState, useEffect } from 'react';
import { 
  PlusIcon, 
  PencilIcon, 
  TrashIcon, 
  MagnifyingGlassIcon,
  UserIcon,
  AcademicCapIcon,
  UserGroupIcon,
  XMarkIcon,
  FingerPrintIcon,
  CpuChipIcon,
  DocumentArrowUpIcon
} from '@heroicons/react/24/outline';
import axios from 'axios';
import toast from 'react-hot-toast';
import { useAuth } from '../contexts/AuthContext';

function Users() {
  const { user } = useAuth();
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [editingUser, setEditingUser] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterType, setFilterType] = useState('all');
  const [filterStatus, setFilterStatus] = useState('all');
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [testingAttendance, setTestingAttendance] = useState(null);
  const [showUploadModal, setShowUploadModal] = useState(false);
  const [uploadFile, setUploadFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [uploadResults, setUploadResults] = useState(null);
  const [previewData, setPreviewData] = useState(null);
  const [showPreview, setShowPreview] = useState(false);
  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    email: '',
    password: '',
    user_type: 'student',
    student_id: '',
    employee_id: '',
    year_level: '',
    department: '',
    status: 'active'
  });

  useEffect(() => {
    fetchUsers();
  }, [currentPage, filterType, filterStatus, searchTerm]);

  const fetchUsers = async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams({
        page: currentPage,
        limit: 20
      });

      if (filterType !== 'all') {
        params.append('type', filterType);
      }
      if (filterStatus !== 'all') {
        params.append('status', filterStatus);
      }
      if (searchTerm) {
        params.append('search', searchTerm);
      }

      const response = await axios.get(`http://localhost:5000/api/users?${params}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });

      setUsers(response.data.users);
      setTotalPages(response.data.pagination.pages);
    } catch (error) {
      /* eslint-disable */console.error(...oo_tx(`4167829341_79_6_79_51_11`,'Error fetching users:', error));
      toast.error('Failed to load users');
    } finally {
      setLoading(false);
    }
  };

  const handleTestAttendance = async (userId, authMethod = 'RFID') => {
    try {
      setTestingAttendance(userId);

      const response = await axios.post(
        `http://localhost:5000/api/users/${userId}/test-attendance`,
        { 
          authMethod: authMethod,
          location: 'inside'
        },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        }
      );

      const { attendance } = response.data;

      toast.success(
        <div>
          <div className="font-medium"> Attendance Recorded!</div>
          <div className="text-sm mt-1">
            <div><strong>{attendance.user}</strong></div>
            <div>{attendance.course} - {attendance.room}</div>
            <div>Status: <span className={`font-medium ${
              attendance.status === 'Present' ? 'text-green-600' : 
              attendance.status === 'Late' ? 'text-yellow-600' : 'text-red-600'
            }`}>{attendance.status}</span></div>
            <div>Auth: {attendance.authMethod}</div>
          </div>
        </div>,
        { duration: 4000 }
      );
    } catch (error) {
      /* eslint-disable */console.error(...oo_tx(`4167829341_119_6_119_55_11`,'Error testing attendance:', error));
