import React, { useState, useEffect } from 'react';
import { PlusIcon, PencilIcon, TrashIcon, ClockIcon, UserGroupIcon } from '@heroicons/react/24/outline';
import toast from 'react-hot-toast';

function Schedules() {
  const [schedules, setSchedules] = useState([]);
  const [courses, setCourses] = useState([]);
  const [instructors, setInstructors] = useState([]);
  const [rooms, setRooms] = useState([]);
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [editingSchedule, setEditingSchedule] = useState(null);
  const [showEnrollModal, setShowEnrollModal] = useState(false);
  const [selectedSchedule, setSelectedSchedule] = useState(null);

  const [formData, setFormData] = useState({
    COURSEID: '',
    ROOMID: '',
    DAYOFWEEK: 'Monday',
    STARTTIME: '',
    ENDTIME: '',
    ACADEMICYEAR: '2024-2025',
    SEMESTER: 'First Semester'
  });

  const daysOfWeek = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];
  const semesters = ['First Semester', 'Second Semester', 'Summer'];

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      // Mock data for now since backend might not be fully connected
      setSchedules([
        {
          SCHEDULEID: '1',
          COURSECODE: 'CS101',
          COURSENAME: 'Introduction to Computer Science',
          instructor_name: 'Dr. John Smith',
          ROOMNUMBER: 'A101',
          ROOMNAME: 'Computer Lab 1',
          DAYOFWEEK: 'Monday',
          STARTTIME: '08:00:00',
          ENDTIME: '10:00:00',
          ACADEMICYEAR: '2024-2025',
          SEMESTER: 'First Semester'
        },
        {
          SCHEDULEID: '2',
          COURSECODE: 'MATH101',
          COURSENAME: 'Calculus I',
          instructor_name: 'Prof. Jane Doe',
          ROOMNUMBER: 'B201',
          ROOMNAME: 'Mathematics Room',
          DAYOFWEEK: 'Tuesday',
          STARTTIME: '10:00:00',
          ENDTIME: '12:00:00',
          ACADEMICYEAR: '2024-2025',
          SEMESTER: 'First Semester'
        }
      ]);

      setCourses([
        { COURSEID: '1', COURSECODE: 'CS101', COURSENAME: 'Introduction to Computer Science' },
        { COURSEID: '2', COURSECODE: 'MATH101', COURSENAME: 'Calculus I' },
        { COURSEID: '3', COURSECODE: 'ENG101', COURSENAME: 'English Composition' }
      ]);

      setInstructors([
        { USERID: '1', FIRSTNAME: 'John', LASTNAME: 'Smith' },
        { USERID: '2', FIRSTNAME: 'Jane', LASTNAME: 'Doe' },
        { USERID: '3', FIRSTNAME: 'Bob', LASTNAME: 'Johnson' }
      ]);

      setRooms([
        { ROOMID: '1', ROOMNUMBER: 'A101', ROOMNAME: 'Computer Lab 1' },
        { ROOMID: '2', ROOMNUMBER: 'B201', ROOMNAME: 'Mathematics Room' },
        { ROOMID: '3', ROOMNUMBER: 'C301', ROOMNAME: 'English Classroom' }
      ]);

      setStudents([
        { USERID: '1', FIRSTNAME: 'Alice', LASTNAME: 'Wilson', STUDENTID: 'S001', EMAIL: 'alice@liceo.edu.ph' },
        { USERID: '2', FIRSTNAME: 'Bob', LASTNAME: 'Brown', STUDENTID: 'S002', EMAIL: 'bob@liceo.edu.ph' },
        { USERID: '3', FIRSTNAME: 'Carol', LASTNAME: 'Davis', STUDENTID: 'S003', EMAIL: 'carol@liceo.edu.ph' }
      ]);
    } catch (error) {
      console.error('Error fetching data:', error);
      toast.error('Failed to load data');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Mock submission for now
      const newSchedule = {
        SCHEDULEID: Date.now().toString(),
        ...formData,
        COURSECODE: courses.find(c => c.COURSEID === formData.COURSEID)?.COURSECODE || '',
        COURSENAME: courses.find(c => c.COURSEID === formData.COURSEID)?.COURSENAME || '',
        instructor_name: instructors.find(i => i.USERID === formData.INSTRUCTORID)?.FIRSTNAME + ' ' + instructors.find(i => i.USERID === formData.INSTRUCTORID)?.LASTNAME || '',
        ROOMNUMBER: rooms.find(r => r.ROOMID === formData.ROOMID)?.ROOMNUMBER || '',
        ROOMNAME: rooms.find(r => r.ROOMID === formData.ROOMID)?.ROOMNAME || ''
      };

      if (editingSchedule) {
        setSchedules(schedules.map(s => s.SCHEDULEID === editingSchedule.SCHEDULEID ? newSchedule : s));
        toast.success('Schedule updated successfully');
      } else {
        setSchedules([...schedules, newSchedule]);
        toast.success('Schedule created successfully');
      }
      
      setShowModal(false);
      setEditingSchedule(null);
      resetForm();
    } catch (error) {
      console.error('Error saving schedule:', error);
      toast.error('Failed to save schedule');
    }
  };

  const resetForm = () => {
    setFormData({
      COURSEID: '',
      ROOMID: '',
      DAYOFWEEK: 'Monday',
      STARTTIME: '',
      ENDTIME: '',
      ACADEMICYEAR: '2024-2025',
      SEMESTER: 'First Semester'
    });
  };

  const handleEdit = (schedule) => {
    setEditingSchedule(schedule);
    setFormData({
      COURSEID: schedule.COURSEID || '',
      ROOMID: schedule.ROOMID || '',
      DAYOFWEEK: schedule.DAYOFWEEK,
      STARTTIME: schedule.STARTTIME ? schedule.STARTTIME.substring(0, 5) : '', // Convert to HH:MM format
      ENDTIME: schedule.ENDTIME ? schedule.ENDTIME.substring(0, 5) : '', // Convert to HH:MM format
      ACADEMICYEAR: schedule.ACADEMICYEAR,
      SEMESTER: schedule.SEMESTER
    });
    setShowModal(true);
  };

  const handleDelete = async (scheduleId) => {
    if (window.confirm('Are you sure you want to delete this schedule?')) {
      try {
        setSchedules(schedules.filter(s => s.SCHEDULEID !== scheduleId));
        toast.success('Schedule deleted successfully');
      } catch (error) {
        console.error('Error deleting schedule:', error);
        toast.error('Failed to delete schedule');
      }
    }
  };

  const handleManageEnrollment = (schedule) => {
    setSelectedSchedule(schedule);
    setShowEnrollModal(true);
  };

  const formatTime = (time) => {
    if (!time) return '';
    const [hours, minutes] = time.split(':');
    const hour = parseInt(hours);
    const ampm = hour >= 12 ? 'PM' : 'AM';
    const displayHour = hour % 12 || 12;
    return `${displayHour}:${minutes} ${ampm}`;
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Schedule Management</h1>
          <p className="text-gray-600">Manage class schedules, subjects, and enrollment</p>
        </div>
        <button
          onClick={() => setShowModal(true)}
          className="inline-flex items-center px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700"
        >
          <PlusIcon className="h-4 w-4 mr-2" />
          Add Schedule
        </button>
      </div>

      {/* Schedule Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {schedules.map((schedule) => (
          <div key={schedule.SCHEDULEID} className="bg-white rounded-lg shadow border border-gray-200 p-6">
            <div className="flex justify-between items-start mb-4">
              <div>
                <h3 className="text-lg font-semibold text-gray-900">{schedule.COURSECODE}</h3>
                <p className="text-sm text-gray-600">{schedule.COURSENAME}</p>
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={() => handleEdit(schedule)}
                  className="text-gray-400 hover:text-blue-600"
                >
                  <PencilIcon className="h-4 w-4" />
                </button>
                <button
                  onClick={() => handleDelete(schedule.SCHEDULEID)}
                  className="text-gray-400 hover:text-red-600"
                >
                  <TrashIcon className="h-4 w-4" />
                </button>
              </div>
            </div>

            <div className="space-y-2 mb-4">
              <div className="flex items-center text-sm text-gray-600">
                <ClockIcon className="h-4 w-4 mr-2" />
                {schedule.DAYOFWEEK} â€¢ {formatTime(schedule.STARTTIME)} - {formatTime(schedule.ENDTIME)}
              </div>
              <div className="text-sm text-gray-600">
                <strong>Instructor:</strong> {schedule.instructor_name}
              </div>
              <div className="text-sm text-gray-600">
                <strong>Room:</strong> {schedule.ROOMNUMBER} - {schedule.ROOMNAME}
              </div>
              <div className="text-sm text-gray-600">
                <strong>Semester:</strong> {schedule.SEMESTER} ({schedule.ACADEMICYEAR})
              </div>
            </div>

            <button
              onClick={() => handleManageEnrollment(schedule)}
              className="w-full inline-flex items-center justify-center px-3 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 bg-white hover:bg-gray-50"
            >
              <UserGroupIcon className="h-4 w-4 mr-2" />
              Manage Class List
            </button>
          </div>
        ))}
      </div>

      {schedules.length === 0 && (
        <div className="text-center py-12">
          <ClockIcon className="mx-auto h-12 w-12 text-gray-400" />
          <h3 className="mt-2 text-sm font-medium text-gray-900">No schedules</h3>
          <p className="mt-1 text-sm text-gray-500">Get started by creating a new class schedule.</p>
        </div>
      )}

      {/* Create/Edit Schedule Modal */}
      {showModal && (
        <div className="fixed inset-0 z-50 overflow-y-auto">
          <div className="flex items-center justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
            <div className="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity"></div>
            
            <div className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
              <form onSubmit={handleSubmit}>
                <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                  <h3 className="text-lg font-medium text-gray-900 mb-4">
                    {editingSchedule ? 'Edit Schedule' : 'Create New Schedule'}
                  </h3>
                  
                  <div className="grid grid-cols-1 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700">Course</label>
                      <select
                        value={formData.COURSEID}
                        onChange={(e) => setFormData({...formData, COURSEID: e.target.value})}
                        className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                        required
                      >
                        <option value="">Select Course</option>
                        {courses.map(course => (
                          <option key={course.COURSEID} value={course.COURSEID}>
                            {course.COURSECODE} - {course.COURSENAME}
                          </option>
                        ))}
                      </select>
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700">Room</label>
                      <select
                        value={formData.ROOMID}
                        onChange={(e) => setFormData({...formData, ROOMID: e.target.value})}
                        className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                        required
                      >
                        <option value="">Select Room</option>
                        {rooms.map(room => (
                          <option key={room.ROOMID} value={room.ROOMID}>
                            {room.ROOMNUMBER} - {room.ROOMNAME}
                          </option>
                        ))}
                      </select>
                    </div>

                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <label className="block text-sm font-medium text-gray-700">Day</label>
                        <select
                          value={formData.DAYOFWEEK}
                          onChange={(e) => setFormData({...formData, DAYOFWEEK: e.target.value})}
                          className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                        >
                          {daysOfWeek.map(day => (
                            <option key={day} value={day}>{day}</option>
                          ))}
                        </select>
                      </div>

                      <div>
                        <label className="block text-sm font-medium text-gray-700">Semester</label>
                        <select
                          value={formData.SEMESTER}
                          onChange={(e) => setFormData({...formData, SEMESTER: e.target.value})}
                          className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                        >
                          {semesters.map(semester => (
                            <option key={semester} value={semester}>{semester}</option>
                          ))}
                        </select>
                      </div>
                    </div>

                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <label className="block text-sm font-medium text-gray-700">Start Time</label>
                        <input
                          type="time"
                          value={formData.STARTTIME}
                          onChange={(e) => setFormData({...formData, STARTTIME: e.target.value})}
                          className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                          required
                        />
                      </div>

                      <div>
                        <label className="block text-sm font-medium text-gray-700">End Time</label>
                        <input
                          type="time"
                          value={formData.ENDTIME}
                          onChange={(e) => setFormData({...formData, ENDTIME: e.target.value})}
                          className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                          required
                        />
                      </div>
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700">Academic Year</label>
                      <input
                        type="text"
                        value={formData.ACADEMICYEAR}
                        onChange={(e) => setFormData({...formData, ACADEMICYEAR: e.target.value})}
                        className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                        placeholder="e.g., 2024-2025"
                        required
                      />
                    </div>
                  </div>
                </div>

                <div className="bg-gray-50 px-4 py-3 sm:px-6 sm:flex sm:flex-row-reverse">
                  <button
                    type="submit"
                    className="w-full inline-flex justify-center rounded-md border border-transparent shadow-sm px-4 py-2 bg-blue-600 text-base font-medium text-white hover:bg-blue-700 focus:outline-none sm:ml-3 sm:w-auto sm:text-sm"
                  >
                    {editingSchedule ? 'Update' : 'Create'}
                  </button>
                  <button
                    type="button"
                    onClick={() => {
                      setShowModal(false);
                      setEditingSchedule(null);
                      resetForm();
                    }}
                    className="mt-3 w-full inline-flex justify-center rounded-md border border-gray-300 shadow-sm px-4 py-2 bg-white text-base font-medium text-gray-700 hover:bg-gray-50 focus:outline-none sm:mt-0 sm:ml-3 sm:w-auto sm:text-sm"
                  >
                    Cancel
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* Enrollment Management Modal */}
      {showEnrollModal && selectedSchedule && (
        <div className="fixed inset-0 z-50 overflow-y-auto">
          <div className="flex items-center justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
            <div className="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity"></div>
            
            <div className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-2xl sm:w-full">
              <div className="bg-white px-4 pt-5 pb-4 sm:p-6">
                <h3 className="text-lg font-medium text-gray-900 mb-4">
                  Manage Class List - {selectedSchedule.COURSECODE}
                </h3>
                
                <div className="max-h-96 overflow-y-auto">
                  <div className="space-y-2">
                    {students.map(student => (
                      <div key={student.USERID} className="flex items-center justify-between p-3 border border-gray-200 rounded-lg">
                        <div>
                          <p className="font-medium">{student.FIRSTNAME} {student.LASTNAME}</p>
                          <p className="text-sm text-gray-500">{student.STUDENTID} â€¢ {student.EMAIL}</p>
                        </div>
                        <button className="px-3 py-1 text-sm bg-blue-100 text-blue-700 rounded-md hover:bg-blue-200">
                          Enroll
                        </button>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="mt-6 flex justify-end">
                  <button
                    onClick={() => {
                      setShowEnrollModal(false);
                      setSelectedSchedule(null);
                    }}
                    className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-50"
                  >
                    Close
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Schedules;
