              <div>
                <h3 className="text-lg font-semibold text-gray-900 mb-3">Subject Description</h3>
                <p className="text-sm text-gray-600 bg-gray-50 rounded p-4">
                  {modalData.schedule.subject_description}
                </p>
              </div>
