                      <span className="text-sm text-gray-600"> or drag and drop</span>
                      <input
                        id="pdf-upload"
                        ref={fileInputRef}
                        type="file"
                        accept=".pdf"
                        onChange={handleFileSelect}
                        className="sr-only"
                      />
                    </label>
                  </div>
                  <p className="text-xs text-gray-500">PDF files only, up to 10MB</p>
                </div>
