        <div className="bg-white rounded-lg shadow border border-gray-200 p-4">
          <nav className="flex items-center space-x-2 text-sm">
            <button
              onClick={backToMainView}
              className="text-blue-600 hover:text-blue-800 flex items-center"
            >
