import React, { useState, useEffect, useRef } from 'react';
import { createPortal } from 'react-dom';
import { 
  BuildingOfficeIcon,
  BookOpenIcon,
  CalendarDaysIcon,
  MagnifyingGlassIcon,
  EyeIcon,
  XMarkIcon,
  ClockIcon,
  UserGroupIcon,
  MapPinIcon,
  AcademicCapIcon,
  ChevronDownIcon,
  ChevronUpIcon,
  UsersIcon,
  DocumentArrowUpIcon,
  CloudArrowUpIcon,
  DocumentIcon,
  CheckCircleIcon,
  ArrowDownTrayIcon,
  TrashIcon,
  ExclamationTriangleIcon
} from '@heroicons/react/24/outline';
import toast from 'react-hot-toast';
import axios from 'axios';

// Set up axios defaults
axios.defaults.baseURL = 'http://localhost:5000';

function UnifiedManagement() {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [sortBy, setSortBy] = useState('rooms'); // 'rooms', 'subjects', 'schedules'
  const [selectedAcademicYear, setSelectedAcademicYear] = useState('');
  const [selectedSemester, setSelectedSemester] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [modalData, setModalData] = useState(null);
  const [modalType, setModalType] = useState(''); // 'room', 'subject', 'schedule'
  const [expandedCards, setExpandedCards] = useState(new Set());
  const [studentSearchTerm, setStudentSearchTerm] = useState('');

  // Bulk operations states
  const [selectedItems, setSelectedItems] = useState(new Set());
  const [selectAll, setSelectAll] = useState(false);
  const [showBulkDeleteModal, setShowBulkDeleteModal] = useState(false);
  const [bulkDeleteType, setBulkDeleteType] = useState('');
  const [isBulkDeleting, setIsBulkDeleting] = useState(false);

  // PDF Import states
  const [showImportSection, setShowImportSection] = useState(false);
  const [pdfFile, setPdfFile] = useState(null);
  const [dragActive, setDragActive] = useState(false);
  const [isUploading, setIsUploading] = useState(false);
  const [isImporting, setIsImporting] = useState(false);
  const [parsedData, setParsedData] = useState(null);
  const [importResults, setImportResults] = useState(null);
  const [showPreview, setShowPreview] = useState(false);
  const [showDataModal, setShowDataModal] = useState(false);
  const [activeTab, setActiveTab] = useState('subjects');
  const [importOptions, setImportOptions] = useState({
    updateExisting: false,
    createMissingRooms: true,
    skipDuplicates: true
  });

  const fileInputRef = useRef(null);

  // Delete functionality states
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [deleteItem, setDeleteItem] = useState(null);
  const [isDeleting, setIsDeleting] = useState(false);
  const [modalAnimation, setModalAnimation] = useState('hidden');

  // TEMPORARY: Schedule editing states
  const [showEditModal, setShowEditModal] = useState(false);
  const [editingSchedule, setEditingSchedule] = useState(null);
  const [editFormData, setEditFormData] = useState({
    subject_id: '',
    room_id: '',
    day_of_week: '',
    start_time: '',
    end_time: '',
    academic_year: '',
    semester: ''
  });

  useEffect(() => {
    fetchData();
  }, [selectedAcademicYear, selectedSemester]);

  const fetchData = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      if (!token) {
        toast.error('Please login to access data');
        return;
      }

      const headers = { Authorization: `Bearer ${token}` };
      const params = {};

      if (selectedAcademicYear) params.academic_year = selectedAcademicYear;
      if (selectedSemester) params.semester = selectedSemester;

      const response = await axios.get('/api/unified/data', { 
        headers,
        params
      });

      setData(response.data);

      // Set default academic year if not selected
      if (!selectedAcademicYear && response.data.filters.academic_years.length > 0) {
        setSelectedAcademicYear(response.data.filters.academic_years[0]);
      }

    } catch (error) {
      /* eslint-disable */console.error(...oo_tx(`3238698568_122_6_122_50_11`,'Error fetching data:', error));
      if (error.response?.status === 401) {
        toast.error('Session expired. Please login again.');
        localStorage.removeItem('token');
        window.location.href = '/login';
      } else {
        toast.error('Failed to load data');
      }
    } finally {
      setLoading(false);
    }
  };

  const fetchDetailedData = async (id, type) => {
    try {
      const token = localStorage.getItem('token');
      const headers = { Authorization: `Bearer ${token}` };

      let endpoint;
      switch (type) {
        case 'room':
          endpoint = `/api/unified/room/${id}`;
          break;
        case 'subject':
          endpoint = `/api/unified/subject/${id}`;
          break;
        case 'schedule':
          endpoint = `/api/unified/schedule/${id}`;
          break;
        default:
          throw new Error('Invalid type');
      }

      const params = {};
      if (selectedAcademicYear) params.academic_year = selectedAcademicYear;
      if (selectedSemester) params.semester = selectedSemester;

      const response = await axios.get(endpoint, { headers, params });

      setModalData(response.data);
      setModalType(type);
      setShowModal(true);
    } catch (error) {
      /* eslint-disable */console.error(...oo_tx(`3238698568_165_6_165_59_11`,'Error fetching detailed data:', error));
      toast.error('Failed to load detailed information');
    }
  };

  const toggleCardExpansion = (id) => {
    setExpandedCards(prev => {
      const newSet = new Set();
      if (!prev.has(id)) {
        newSet.add(id);
      }
      return newSet;
    });
  };

  // Delete functionality
  const handleDelete = (item, type) => {
    let title, message, confirmText;

    switch (type) {
      case 'room':
        title = 'Delete Room';
        message = `Are you sure you want to delete "${item.ROOMNUMBER} - ${item.ROOMNAME}"? This action cannot be undone.`;
        confirmText = 'Delete Room';
        break;
      case 'subject':
        title = 'Delete Subject';
        message = `Are you sure you want to delete "${item.SUBJECTCODE} - ${item.SUBJECTNAME}"? This will also remove all related schedules and enrollments. This action cannot be undone.`;
        confirmText = 'Delete Subject';
        break;
      case 'schedule':
        title = 'Delete Schedule';
        message = `Are you sure you want to delete this schedule? This action cannot be undone.`;
        confirmText = 'Delete Schedule';
        break;
      default:
        title = 'Delete Item';
        message = 'Are you sure you want to delete this item? This action cannot be undone.';
        confirmText = 'Delete';
    }

    setDeleteItem({
      ...item,
      type,
      title,
      message,
      confirmText
    });
    setShowDeleteModal(true);
    setTimeout(() => setModalAnimation('visible'), 10);
  };

  const confirmDelete = async () => {
    if (!deleteItem) return;

    setIsDeleting(true);

    try {
      const token = localStorage.getItem('token');
      const headers = { Authorization: `Bearer ${token}` };

      let endpoint;
      let successMessage;

      switch (deleteItem.type) {
        case 'room':
          endpoint = `/api/rooms/${deleteItem.ROOMID}`;
          successMessage = 'Room deleted successfully!';
          break;
        case 'subject':
          endpoint = `/api/subjects/${deleteItem.SUBJECTID}`;
          successMessage = 'Subject deleted successfully!';
          break;
        case 'schedule':
          endpoint = `/api/schedules/${deleteItem.SCHEDULEID}`;
          successMessage = 'Schedule deleted successfully!';
          break;
        default:
          throw new Error('Invalid delete type');
      }

      const response = await axios.delete(endpoint, { headers });

      // Show enhanced success message
      const message = response.data.message || successMessage;
      toast.success(message, {
        duration: 4000,
        style: {
          background: '#f0fdf4',
          border: '1px solid #bbf7d0',
          color: '#166534',
        },
      });

      // Refresh data
      await fetchData();

    } catch (error) {
      /* eslint-disable */console.error(...oo_tx(`3238698568_263_6_263_50_11`,'Error deleting item:', error));
      const errorMessage = error.response?.data?.message || 'Failed to delete item';
      const errorDetails = error.response?.data?.details;

      // Show detailed error message
      if (errorDetails) {
        toast.error(`${errorMessage}: ${errorDetails}`, {
          duration: 8000,
          style: {
            background: '#fef2f2',
            border: '1px solid #fecaca',
            color: '#dc2626',
          },
        });
      } else {
        toast.error(errorMessage);
      }
    } finally {
      setIsDeleting(false);
      setModalAnimation('hidden');
      setTimeout(() => {
        setShowDeleteModal(false);
        setDeleteItem(null);
      }, 300);
    }
  };

  // Bulk Delete Functions
  const handleSelectItem = (itemId) => {
    const newSelectedItems = new Set(selectedItems);
    if (newSelectedItems.has(itemId)) {
      newSelectedItems.delete(itemId);
    } else {
      newSelectedItems.add(itemId);
    }
    setSelectedItems(newSelectedItems);

    // Update select all state
    const currentItems = getCurrentItems();
    setSelectAll(newSelectedItems.size === currentItems.length && Array.isArray(currentItems) && currentItems.length > 0);
  };

  const handleSelectAll = () => {
    const currentItems = getCurrentItems();

    if (!Array.isArray(currentItems) || currentItems.length === 0) {
      return;
    }

    if (selectAll) {
      setSelectedItems(new Set());
    } else {
      try {
        const itemIds = currentItems.map(item => getItemId(item));
        setSelectedItems(new Set(itemIds));
      } catch (error) {
        /* eslint-disable */console.error(...oo_tx(`3238698568_319_8_319_52_11`,'Error mapping items:', error));
        return;
      }
    }
    setSelectAll(!selectAll);
  };

  const getCurrentItems = () => {
    if (!data) return [];

    switch (sortBy) {
      case 'rooms':
        return Array.isArray(data.rooms?.data) ? data.rooms.data : [];
      case 'subjects':
        return Array.isArray(data.subjects?.data) ? data.subjects.data : [];
      case 'schedules':
        return Array.isArray(data.schedules?.data) ? data.schedules.data : [];
      default:
        return [];
    }
  };

  const getItemId = (item) => {
    switch (sortBy) {
      case 'rooms':
        return item.ROOMID;
      case 'subjects':
        return item.SUBJECTID;
      case 'schedules':
        // Create unique key for schedule groups
        return `schedule-${item.subject_code}-${item.schedules[0]?.SCHEDULEID || 'default'}`;
      default:
        return item.id;
    }
  };

  const handleBulkDelete = () => {
    if (selectedItems.size === 0) return;

    setBulkDeleteType(sortBy);
    setShowBulkDeleteModal(true);
    setTimeout(() => setModalAnimation('visible'), 10);
  };

  const confirmBulkDelete = async () => {
    if (selectedItems.size === 0) return;

    setIsBulkDeleting(true);

    try {
      const token = localStorage.getItem('token');
      const headers = { Authorization: `Bearer ${token}` };

      let endpoint;
      let successMessage;

      switch (bulkDeleteType) {
        case 'rooms':
          endpoint = '/api/rooms/bulk-delete';
          successMessage = `${selectedItems.size} rooms deleted successfully!`;
          break;
        case 'subjects':
          endpoint = '/api/subjects/bulk-delete';
          successMessage = `${selectedItems.size} subjects deleted successfully!`;
          break;
        case 'schedules':
          endpoint = '/api/schedules/bulk-delete';
          successMessage = `${selectedItems.size} schedules deleted successfully!`;
          break;
        default:
          throw new Error('Invalid bulk delete type');
      }

      const response = await axios.delete(endpoint, {
        headers,
        data: { ids: Array.from(selectedItems) }
      });

      const message = response.data.message || successMessage;
      toast.success(message, {
        duration: 4000,
        style: {
          background: '#f0fdf4',
          border: '1px solid #bbf7d0',
          color: '#166534',
        },
      });

      // Clear selections and refresh data
      setSelectedItems(new Set());
      setSelectAll(false);
      await fetchData();

    } catch (error) {
      /* eslint-disable */console.error(...oo_tx(`3238698568_413_6_413_56_11`,'Error bulk deleting items:', error));
      const errorMessage = error.response?.data?.message || 'Failed to delete items';
      const errorDetails = error.response?.data?.details;

      if (errorDetails) {
        toast.error(`${errorMessage}: ${errorDetails}`, {
          duration: 8000,
          style: {
            background: '#fef2f2',
            border: '1px solid #fecaca',
            color: '#dc2626',
          },
        });
      } else {
        toast.error(errorMessage);
      }
    } finally {
      setIsBulkDeleting(false);
      setModalAnimation('hidden');
      setTimeout(() => {
        setShowBulkDeleteModal(false);
        setBulkDeleteType('');
      }, 300);
    }
  };

  // PDF Import Functions
  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setDragActive(true);
    } else if (e.type === 'dragleave') {
      setDragActive(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      const droppedFile = e.dataTransfer.files[0];
      if (droppedFile.type === 'application/pdf') {
        setPdfFile(droppedFile);
      } else {
        toast.error('Please upload a PDF file');
      }
    }
  };

  const handleFileSelect = (e) => {
    if (e.target.files && e.target.files[0]) {
      setPdfFile(e.target.files[0]);
    }
  };

  const handleUploadAndPreview = async () => {
    if (!pdfFile) {
      toast.error('Please select a PDF file');
      return;
    }

    setIsUploading(true);
    setParsedData(null);
    setImportResults(null);

    try {
      const formData = new FormData();
      formData.append('pdf', pdfFile);

      const response = await axios.post('/api/import/preview', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });

      setParsedData(response.data.data);
      setShowPreview(true);
      toast.success('PDF parsed successfully! Review the data before importing.');

    } catch (error) {
      /* eslint-disable */console.error(...oo_tx(`3238698568_497_6_497_43_11`,'Upload error:', error));
      if (error.response?.data?.message) {
        toast.error(error.response.data.message);
      } else {
        toast.error('Failed to parse PDF');
      }
    } finally {
      setIsUploading(false);
    }
  };

  const handleImport = async () => {
    if (!parsedData) {
      toast.error('No data to import');
      return;
    }

    setIsImporting(true);

    try {
      const response = await axios.post('/api/import/execute', {
        parsedData,
        options: importOptions
      }, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });

      setImportResults(response.data.results);
      toast.success('Data imported successfully! Refreshing data...');

      // Refresh the main data after import
      await fetchData();

      // Reset import form
      resetImportForm();

    } catch (error) {
      /* eslint-disable */console.error(...oo_tx(`3238698568_536_6_536_43_11`,'Import error:', error));
      if (error.response?.data?.message) {
        toast.error(error.response.data.message);
      } else {
        toast.error('Failed to import data');
      }
    } finally {
      setIsImporting(false);
    }
  };

  const resetImportForm = () => {
    setPdfFile(null);
    setParsedData(null);
    setImportResults(null);
    setShowPreview(false);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  // Delete functionality
  const handleDeleteClick = (item, type) => {
    setDeleteItem({ ...item, type });
    setShowDeleteModal(true);
  };

  const handleDeleteConfirm = async () => {
    if (!deleteItem) return;

    setIsDeleting(true);
    try {
      let endpoint = '';
      let idField = '';

      switch (deleteItem.type) {
        case 'room':
          endpoint = '/api/rooms';
          idField = 'roomId';
          break;
        case 'subject':
          endpoint = '/api/subjects';
          idField = 'subjectId';
          break;
        case 'schedule':
          endpoint = '/api/schedules';
          idField = 'scheduleId';
          break;
        default:
          throw new Error('Invalid delete type');
      }

      await axios.delete(`${endpoint}/${deleteItem[idField]}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });

      toast.success(`${deleteItem.type.charAt(0).toUpperCase() + deleteItem.type.slice(1)} deleted successfully!`);

      // Refresh data
      await fetchData();

      // Close modal
      setShowDeleteModal(false);
      setDeleteItem(null);

    } catch (error) {
      /* eslint-disable */console.error(...oo_tx(`3238698568_604_6_604_43_11`,'Delete error:', error));
      if (error.response?.data?.message) {
        toast.error(error.response.data.message);
      } else {
        toast.error(`Failed to delete ${deleteItem.type}`);
      }
    } finally {
      setIsDeleting(false);
    }
  };

  const handleDeleteCancel = () => {
    setShowDeleteModal(false);
    setDeleteItem(null);
  };

  // TEMPORARY: Schedule editing functions
  const handleEditSchedule = (schedule) => {
    setEditingSchedule(schedule);
    setEditFormData({
      subject_id: schedule.SUBJECTID || '',
      room_id: schedule.ROOMID || '',
      day_of_week: schedule.DAYOFWEEK || '',
      start_time: schedule.STARTTIME ? schedule.STARTTIME.substring(0, 5) : '',
      end_time: schedule.ENDTIME ? schedule.ENDTIME.substring(0, 5) : '',
      academic_year: schedule.ACADEMICYEAR || '',
      semester: schedule.SEMESTER || ''
    });
    setShowEditModal(true);
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();

    if (!editingSchedule) return;

    try {
      const token = localStorage.getItem('token');
      const headers = { Authorization: `Bearer ${token}` };

      const updateData = {
        subject_id: editFormData.subject_id,
        room_id: editFormData.room_id,
        day_of_week: editFormData.day_of_week,
        start_time: editFormData.start_time,
        end_time: editFormData.end_time,
        academic_year: editFormData.academic_year,
        semester: editFormData.semester
      };

      await axios.put(`/api/schedules/${editingSchedule.SCHEDULEID}`, updateData, { headers });
      toast.success('Schedule updated successfully!');

      // Refresh data
      await fetchData();

      // Close modal
      setShowEditModal(false);
      setEditingSchedule(null);
      setEditFormData({
        subject_id: '',
        room_id: '',
        day_of_week: '',
        start_time: '',
        end_time: '',
        academic_year: '',
        semester: ''
      });
    } catch (error) {
      /* eslint-disable */console.error(...oo_tx(`3238698568_673_6_673_54_11`,'Error updating schedule:', error));
      if (error.response?.data?.message) {
        toast.error(error.response.data.message);
      } else {
        toast.error('Failed to update schedule');
      }
    }
  };

  const formatTime = (time) => {
    if (!time) return '';
    const [hours, minutes] = time.split(':');
    const hour = parseInt(hours);
    const ampm = hour >= 12 ? 'PM' : 'AM';
    const displayHour = hour % 12 || 12;
    return `${displayHour}:${minutes} ${ampm}`;
  };

  // Collapsible container with measured height for smooth open/close
  const Collapsible = ({ isOpen, children }) => {
    const containerRef = useRef(null);
    const [height, setHeight] = useState('0px');

    useEffect(() => {
      const el = containerRef.current;
      if (!el) return;

      if (isOpen) {
        // From current height to content height
        setHeight(`${el.scrollHeight}px`);
        const onEnd = () => {
          el.removeEventListener('transitionend', onEnd);
          setHeight('auto');
        };
        el.addEventListener('transitionend', onEnd);
      } else {
        // If currently auto, snap to pixel height first
        const current = el.scrollHeight;
        setHeight(`${current}px`);
        // Next frame collapse to zero
        requestAnimationFrame(() => setHeight('0px'));
      }
    }, [isOpen]);

    useEffect(() => {
      const el = containerRef.current;
      if (!el) return;
      el.style.overflow = 'hidden';
    }, []);

    return (
      <div
        ref={containerRef}
        style={{ height, transition: 'height 150ms ease, opacity 150ms ease', opacity: isOpen ? 1 : 0, willChange: 'height' }}
      >
        <div className="pt-2">
          {children}
        </div>
      </div>
    );
  };

  // Helper function to group schedules by subject
  const groupSchedulesBySubject = (schedulesList) => {
    const groups = {};
    schedulesList.forEach(schedule => {
      // Group by subject code only (which includes section like BSIT-4102-BSIT4-1)
      const key = schedule.SUBJECTCODE;
      if (!groups[key]) {
        groups[key] = {
          subject_code: schedule.SUBJECTCODE,
          subject_name: schedule.SUBJECTNAME,
          instructor_name: schedule.instructor_name,
          academic_year: schedule.ACADEMICYEAR,
          semester: schedule.SEMESTER,
          schedules: []
        };
      }
      groups[key].schedules.push(schedule);
    });
    return Object.values(groups);
  };

  const filterStudents = (students, searchTerm) => {
    if (!Array.isArray(students) || !searchTerm) return students;

    const searchLower = searchTerm.toLowerCase();
    return students.filter(student => 
      student.FIRSTNAME?.toLowerCase().includes(searchLower) ||
      student.LASTNAME?.toLowerCase().includes(searchLower) ||
      student.STUDENTID?.toLowerCase().includes(searchLower) ||
      student.DEPARTMENT?.toLowerCase().includes(searchLower) ||
      student.YEARLEVEL?.toString().includes(searchLower)
    );
  };

  const filterData = (items, searchTerm) => {
    if (!Array.isArray(items)) return [];
    if (!searchTerm) return items;

    return items.filter(item => {
      const searchLower = searchTerm.toLowerCase();

      switch (sortBy) {
        case 'rooms':
          return (
            item.ROOMNUMBER?.toLowerCase().includes(searchLower) ||
            item.ROOMNAME?.toLowerCase().includes(searchLower) ||
            item.BUILDING?.toLowerCase().includes(searchLower)
          );
        case 'subjects':
          return (
            item.SUBJECTCODE?.toLowerCase().includes(searchLower) ||
            item.SUBJECTNAME?.toLowerCase().includes(searchLower) ||
            item.instructor_name?.toLowerCase().includes(searchLower)
          );
        case 'schedules':
          return (
            item.subject_code?.toLowerCase().includes(searchLower) ||
            item.subject_name?.toLowerCase().includes(searchLower) ||
            item.instructor_name?.toLowerCase().includes(searchLower) ||
            item.schedules?.some(schedule => 
              schedule.ROOMNUMBER?.toLowerCase().includes(searchLower) ||
              schedule.DAYOFWEEK?.toLowerCase().includes(searchLower)
            )
          );
        default:
          return true;
      }
    });
  };

  const getCurrentData = () => {
    if (!data) return [];

    switch (sortBy) {
      case 'rooms':
        return data.rooms.data || [];
      case 'subjects':
