import React, { useState, useEffect } from 'react';
import { PlusIcon, PencilIcon, TrashIcon, BookOpenIcon, UserGroupIcon } from '@heroicons/react/24/outline';
import axios from 'axios';
import toast from 'react-hot-toast';

function Subjects() {
  const [subjects, setSubjects] = useState([]);
  const [instructors, setInstructors] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [editingSubject, setEditingSubject] = useState(null);

  const [formData, setFormData] = useState({
    SUBJECTCODE: '',
    SUBJECTNAME: '',
    DESCRIPTION: '',
    INSTRUCTORID: '',
    SEMESTER: 'First Semester',
    YEAR: new Date().getFullYear(),
    ACADEMICYEAR: '2024-2025'
  });

  const semesterOptions = ['First Semester', 'Second Semester', 'Summer'];

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      
      // Fetch subjects and instructors in parallel
      const [subjectsResponse, instructorsResponse] = await Promise.all([
        axios.get('/api/subjects', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        }),
        axios.get('/api/subjects/instructors/list', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        })
      ]);

      setSubjects(subjectsResponse.data.subjects || []);
      setInstructors(instructorsResponse.data || []);
      
    } catch (error) {
      console.error('Error fetching data:', error);
      if (error.response?.status === 401) {
        toast.error('Session expired. Please login again.');
        // Redirect to login or refresh token
      } else {
        toast.error('Failed to load subjects');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      const url = editingSubject 
        ? `/api/subjects/${editingSubject.SUBJECTID}` 
        : '/api/subjects';
      
      const method = editingSubject ? 'put' : 'post';
      
      await axios[method](url, formData, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });

      toast.success(`Subject ${editingSubject ? 'updated' : 'created'} successfully!`);
      setShowModal(false);
      resetForm();
      fetchData();
    } catch (error) {
      console.error('Error saving subject:', error);
      if (error.response?.data?.errors) {
        error.response.data.errors.forEach(err => toast.error(err.msg));
      } else {
        toast.error(error.response?.data?.message || 'Failed to save subject');
      }
    }
  };

  const handleEdit = (subject) => {
    setEditingSubject(subject);
    setFormData({
      SUBJECTCODE: subject.SUBJECTCODE || '',
      SUBJECTNAME: subject.SUBJECTNAME || '',
      DESCRIPTION: subject.DESCRIPTION || '',
      INSTRUCTORID: subject.INSTRUCTORID || '',
      SEMESTER: subject.SEMESTER || 'First Semester',
      YEAR: subject.YEAR || new Date().getFullYear(),
      ACADEMICYEAR: subject.ACADEMICYEAR || '2024-2025'
    });
    setShowModal(true);
  };

  const handleDelete = async (subjectId) => {
    if (!window.confirm('Are you sure you want to delete this subject?')) {
      return;
    }

    try {
      await axios.delete(`/api/subjects/${subjectId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      
      toast.success('Subject deleted successfully!');
      fetchData();
    } catch (error) {
      console.error('Error deleting subject:', error);
      toast.error(error.response?.data?.message || 'Failed to delete subject');
    }
  };

  const resetForm = () => {
    setFormData({
      SUBJECTCODE: '',
      SUBJECTNAME: '',
      DESCRIPTION: '',
      INSTRUCTORID: '',
      SEMESTER: 'First Semester',
      YEAR: new Date().getFullYear(),
      ACADEMICYEAR: '2024-2025'
    });
    setEditingSubject(null);
  };

  const closeModal = () => {
    setShowModal(false);
    resetForm();
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="p-6">
      {/* Header */}
      <div className="mb-8">
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Subjects</h1>
            <p className="mt-1 text-sm text-gray-600">
              Manage academic subjects and assign instructors
            </p>
          </div>
          <button
            onClick={() => setShowModal(true)}
            className="inline-flex items-center px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
          >
            <PlusIcon className="h-5 w-5 mr-2" />
            Add Subject
          </button>
        </div>
      </div>

      {/* Subjects Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {subjects.map((subject) => (
          <div key={subject.SUBJECTID} className="bg-white overflow-hidden shadow rounded-lg border border-gray-200 hover:shadow-md transition-shadow">
            <div className="p-6">
              <div className="flex items-center justify-between mb-4">
                <div className="flex items-center">
                  <BookOpenIcon className="h-8 w-8 text-blue-600" />
                  <div className="ml-3">
                    <h3 className="text-lg font-medium text-gray-900">{subject.SUBJECTCODE}</h3>
                    <p className="text-sm text-gray-500">{subject.SEMESTER} {subject.YEAR}</p>
                  </div>
                </div>
                <div className="flex space-x-2">
                  <button
                    onClick={() => handleEdit(subject)}
                    className="p-2 text-gray-400 hover:text-blue-600 transition-colors"
                  >
                    <PencilIcon className="h-4 w-4" />
                  </button>
                  <button
                    onClick={() => handleDelete(subject.SUBJECTID)}
                    className="p-2 text-gray-400 hover:text-red-600 transition-colors"
                  >
                    <TrashIcon className="h-4 w-4" />
                  </button>
                </div>
              </div>
              
              <h4 className="text-base font-semibold text-gray-900 mb-2">
                {subject.SUBJECTNAME}
              </h4>
              
              {subject.DESCRIPTION && (
                <p className="text-sm text-gray-600 mb-4 line-clamp-2">
                  {subject.DESCRIPTION}
                </p>
              )}
              
              <div className="flex items-center justify-between text-sm">
                <div className="flex items-center text-gray-500">
                  <UserGroupIcon className="h-4 w-4 mr-1" />
                  <span>{subject.enrolled_students || 0} students</span>
                </div>
                <span className="text-gray-500">
                  {subject.instructor_name || 'No instructor assigned'}
                </span>
              </div>
              
              <div className="mt-4 text-xs text-gray-400">
                Academic Year: {subject.ACADEMICYEAR}
              </div>
            </div>
          </div>
        ))}
      </div>

      {subjects.length === 0 && (
        <div className="text-center py-12">
          <BookOpenIcon className="mx-auto h-12 w-12 text-gray-400" />
          <h3 className="mt-2 text-sm font-medium text-gray-900">No subjects</h3>
          <p className="mt-1 text-sm text-gray-500">Get started by creating a new subject.</p>
          <div className="mt-6">
            <button
              onClick={() => setShowModal(true)}
              className="inline-flex items-center px-4 py-2 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              <PlusIcon className="h-5 w-5 mr-2" />
              Add Subject
            </button>
          </div>
        </div>
      )}

      {/* Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-11/12 md:w-3/4 lg:w-1/2 shadow-lg rounded-md bg-white">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-medium text-gray-900">
                {editingSubject ? 'Edit Subject' : 'Add New Subject'}
              </h3>
              <button
                onClick={closeModal}
                className="text-gray-400 hover:text-gray-600"
              >
                <span className="sr-only">Close</span>
                <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>

            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700">Subject Code *</label>
                  <input
                    type="text"
                    required
                    value={formData.SUBJECTCODE}
                    onChange={(e) => setFormData({...formData, SUBJECTCODE: e.target.value})}
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                    placeholder="e.g., CS101"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700">Instructor *</label>
                  <select
                    required
                    value={formData.INSTRUCTORID}
                    onChange={(e) => setFormData({...formData, INSTRUCTORID: e.target.value})}
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                  >
                    <option value="">Select Instructor</option>
                    {instructors.map((instructor) => (
                      <option key={instructor.USERID} value={instructor.USERID}>
                        {instructor.FIRSTNAME} {instructor.LASTNAME} ({instructor.EMPLOYEEID || instructor.FACULTYID})
                      </option>
                    ))}
                  </select>
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700">Subject Name *</label>
                <input
                  type="text"
                  required
                  value={formData.SUBJECTNAME}
                  onChange={(e) => setFormData({...formData, SUBJECTNAME: e.target.value})}
                  className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                  placeholder="e.g., Introduction to Computer Science"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700">Description</label>
                <textarea
                  value={formData.DESCRIPTION}
                  onChange={(e) => setFormData({...formData, DESCRIPTION: e.target.value})}
                  rows={3}
                  className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                  placeholder="Subject description (optional)"
                />
              </div>

              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700">Semester *</label>
                  <select
                    required
                    value={formData.SEMESTER}
                    onChange={(e) => setFormData({...formData, SEMESTER: e.target.value})}
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                  >
                    {semesterOptions.map((semester) => (
                      <option key={semester} value={semester}>{semester}</option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700">Year *</label>
                  <input
                    type="number"
                    required
                    min="2020"
                    max="2030"
                    value={formData.YEAR}
                    onChange={(e) => setFormData({...formData, YEAR: parseInt(e.target.value)})}
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700">Academic Year *</label>
                  <input
                    type="text"
                    required
                    pattern="\d{4}-\d{4}"
                    value={formData.ACADEMICYEAR}
                    onChange={(e) => setFormData({...formData, ACADEMICYEAR: e.target.value})}
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                    placeholder="2024-2025"
                  />
                </div>
              </div>

              <div className="flex justify-end space-x-3 pt-4">
                <button
                  type="button"
                  onClick={closeModal}
                  className="px-4 py-2 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                >
                  {editingSubject ? 'Update' : 'Create'} Subject
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default Subjects;
