import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';

function Settings() {
  const { user, changePassword, createPassword } = useAuth();
  const hasPassword = Boolean(user?.has_password);

  const [formValues, setFormValues] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });
  const [formError, setFormError] = useState(null);
  const [submitting, setSubmitting] = useState(false);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormValues((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setFormError(null);

    if (formValues.newPassword.length < 6) {
      setFormError('New password must be at least 6 characters long.');
      return;
    }

    if (formValues.newPassword !== formValues.confirmPassword) {
      setFormError('Password confirmation does not match.');
      return;
    }

    if (hasPassword && !formValues.currentPassword) {
      setFormError('Current password is required.');
      return;
    }

    setSubmitting(true);

    const action = hasPassword
      ? () => changePassword(formValues.currentPassword, formValues.newPassword)
      : () => createPassword(formValues.newPassword, formValues.confirmPassword);

    const result = await action();

    if (!result?.success) {
      setFormError(result?.message || 'Unable to update password. Please try again.');
    } else {
      setFormValues({
        currentPassword: '',
        newPassword: '',
        confirmPassword: ''
      });
    }

    setSubmitting(false);
  };

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-bold text-gray-900">Account Settings</h1>
        <p className="text-gray-600">Review your account details and manage dashboard access credentials.</p>
      </div>

      <div className="grid gap-6 lg:grid-cols-2">
        <div className="card space-y-4">
          <div className="flex items-center justify-between">
            <h2 className="text-lg font-semibold text-gray-900">Account details</h2>
            <span className={`badge ${hasPassword ? 'badge-success' : 'badge-warning'}`}>
              {hasPassword ? 'Password set' : 'No password on file'}
            </span>
          </div>
          <div className="space-y-4">
            <div>
              <p className="text-sm text-gray-500">Email address</p>
              <p className="text-base font-medium text-gray-900">{user?.email || '—'}</p>
            </div>
            <div className="grid gap-3 sm:grid-cols-2">
              <div>
                <p className="text-sm text-gray-500">First name</p>
                <p className="text-base font-medium text-gray-900">{user?.first_name || '—'}</p>
              </div>
              <div>
                <p className="text-sm text-gray-500">Last name</p>
                <p className="text-base font-medium text-gray-900">{user?.last_name || '—'}</p>
              </div>
            </div>
            <div>
              <p className="text-sm text-gray-500">Role</p>
              <p className="text-base font-medium capitalize text-gray-900">{user?.role || '—'}</p>
            </div>
            <div className="rounded-lg border border-gray-200 bg-gray-50 px-4 py-3 text-sm text-gray-600">
              Password authentication supplements Google SSO. If no password is configured, access is still available via institutional Google login.
            </div>
          </div>
        </div>

        <div className="card space-y-4">
          <h2 className="text-lg font-semibold text-gray-900">
            {hasPassword ? 'Update password' : 'Create password'}
          </h2>
          <p className="text-sm text-gray-600">
            {hasPassword
              ? 'Provide your current password to set a new one for email-based login.'
              : 'Choose a new password to enable email-based dashboard access in addition to Google SSO.'}
          </p>

          <form className="space-y-4" onSubmit={handleSubmit}>
            {hasPassword && (
              <div>
                <label htmlFor="currentPassword" className="label">
                  Current password
                </label>
                <input
                  id="currentPassword"
                  name="currentPassword"
                  type="password"
                  className="input"
                  value={formValues.currentPassword}
                  onChange={handleChange}
                  disabled={submitting}
                  autoComplete="current-password"
                />
              </div>
            )}
            <div>
              <label htmlFor="newPassword" className="label">
                New password
              </label>
              <input
                id="newPassword"
                name="newPassword"
                type="password"
                className="input"
                value={formValues.newPassword}
                onChange={handleChange}
                disabled={submitting}
                minLength={6}
                autoComplete={hasPassword ? 'new-password' : 'off'}
                required
              />
            </div>
            <div>
              <label htmlFor="confirmPassword" className="label">
                {hasPassword ? 'Confirm new password' : 'Confirm password'}
              </label>
              <input
                id="confirmPassword"
                name="confirmPassword"
                type="password"
                className="input"
                value={formValues.confirmPassword}
                onChange={handleChange}
                disabled={submitting}
                minLength={6}
                autoComplete={hasPassword ? 'new-password' : 'off'}
                required
              />
            </div>

            {formError && (
              <p className="rounded-lg border border-danger-100 bg-danger-50 px-3 py-2 text-sm text-danger-600">
                {formError}
              </p>
            )}

            <button
              type="submit"
              disabled={submitting}
              className="inline-flex items-center justify-center rounded-lg bg-maroon-600 px-4 py-2 text-sm font-semibold text-white shadow-sm transition-colors duration-200 hover:bg-maroon-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-maroon-500 disabled:opacity-60"
            >
              {submitting ? 'Saving…' : hasPassword ? 'Update password' : 'Create password'}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Settings;
