import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      await login(formData.email, formData.password);
      navigate('/dashboard');
    } catch (err) {
      setError(err.response?.data?.message || 'Login failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div className="min-h-screen flex items-center justify-center ldcu-gradient relative overflow-hidden">
      {/* Background Pattern */}
      <div className="absolute inset-0 opacity-10">
        <div className="absolute top-20 left-20 w-40 h-40 border-2 border-white rounded-full"></div>
        <div className="absolute top-40 right-32 w-32 h-32 border-2 border-white rounded-full"></div>
        <div className="absolute bottom-32 left-1/4 w-24 h-24 border-2 border-white rounded-full"></div>
        <div className="absolute bottom-20 right-20 w-36 h-36 border-2 border-white rounded-full"></div>
      </div>

      <div className="max-w-md w-full space-y-8 p-8 relative z-10">
        {/* Header */}
        <div className="text-center">
          <div className="mx-auto w-24 h-24 bg-white rounded-full flex items-center justify-center mb-6 shadow-lg">
            <img 
              src="/ldcu-logo.png" 
              alt="LDCU" 
              className="w-20 h-20 object-contain"
              onError={(e) => {
                e.target.style.display = 'none';
                e.target.nextSibling.style.display = 'block';
              }}
            />
            <span 
              className="text-3xl font-bold hidden"
              style={{ color: 'var(--ldcu-maroon)' }}
            >
              LDCU
            </span>
          </div>
          <h2 className="text-3xl font-bold text-white">
            Welcome to LDCU
          </h2>
          <p className="mt-2 text-lg text-white/90">
            IoT Attendance Management System
          </p>
          <p className="mt-1 text-sm text-white/70">
            Liceo de Cagayan University
          </p>
        </div>

        {/* Login Form */}
        <div className="ldcu-card p-8 shadow-2xl">
          <form className="space-y-6" onSubmit={handleSubmit}>
            <div className="text-center mb-6">
              <h3 className="text-xl font-semibold" style={{ color: 'var(--ldcu-maroon)' }}>
                Sign in to your account
              </h3>
            </div>

            {error && (
              <div className="bg-red-50 border border-red-200 rounded-lg p-4">
                <div className="flex">
                  <div className="flex-shrink-0">
                    <span className="text-red-400">⚠️</span>
                  </div>
                  <div className="ml-3">
                    <p className="text-sm text-red-700">{error}</p>
                  </div>
                </div>
              </div>
            )}

            <div>
              <label htmlFor="email" className="block text-sm font-medium mb-2" style={{ color: 'var(--ldcu-maroon)' }}>
                Email Address
              </label>
              <input
                id="email"
                name="email"
                type="email"
                autoComplete="email"
                required
                className="ldcu-input w-full"
                placeholder="Enter your email"
                value={formData.email}
                onChange={handleChange}
              />
            </div>

            <div>
              <label htmlFor="password" className="block text-sm font-medium mb-2" style={{ color: 'var(--ldcu-maroon)' }}>
                Password
              </label>
              <input
                id="password"
                name="password"
                type="password"
                autoComplete="current-password"
                required
                className="ldcu-input w-full"
                placeholder="Enter your password"
                value={formData.password}
                onChange={handleChange}
              />
            </div>

            <div className="flex items-center justify-between">
              <div className="flex items-center">
                <input
                  id="remember-me"
                  name="remember-me"
                  type="checkbox"
                  className="h-4 w-4 rounded border-gray-300 focus:ring-2 focus:ring-offset-2"
                  style={{ 
                    accentColor: 'var(--ldcu-maroon)',
                    focusRingColor: 'var(--ldcu-maroon)'
                  }}
                />
                <label htmlFor="remember-me" className="ml-2 block text-sm text-gray-700">
                  Remember me
                </label>
              </div>

              <div className="text-sm">
                <a href="#" className="font-medium hover:underline" style={{ color: 'var(--ldcu-maroon)' }}>
                  Forgot your password?
                </a>
              </div>
            </div>

            <div>
              <button
                type="submit"
                disabled={loading}
                className="ldcu-btn-primary w-full flex justify-center items-center"
              >
                {loading ? (
                  <>
                    <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                    </svg>
                    Signing in...
                  </>
                ) : (
                  'Sign in'
                )}
              </button>
            </div>
          </form>

          <div className="mt-6 text-center">
            <p className="text-sm text-gray-600">
              Need help? Contact{' '}
              <a href="mailto:support@liceo.edu.ph" className="font-medium hover:underline" style={{ color: 'var(--ldcu-maroon)' }}>
                IT Support
              </a>
            </p>
          </div>
        </div>

        {/* Footer */}
        <div className="text-center text-white/70">
          <p className="text-sm">
            © 2024 Liceo de Cagayan University
          </p>
          <p className="text-xs mt-1">
            "Nil Sine Numine" - Nothing Without Divine Providence
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
