import React, { useState, useEffect } from 'react';
import { createPortal } from 'react-dom';
import { 
  PlusIcon, 
  PencilIcon, 
  TrashIcon, 
  MagnifyingGlassIcon,
  UserIcon,
  AcademicCapIcon,
  UserGroupIcon,
  XMarkIcon,
  FingerPrintIcon,
  CpuChipIcon,
  DocumentArrowUpIcon,
  CreditCardIcon,
  CheckIcon,
  ExclamationTriangleIcon,
  EllipsisVerticalIcon,
  ChevronDownIcon,
  Squares2X2Icon,
  RectangleStackIcon
} from '@heroicons/react/24/outline';
import axios from 'axios';
import toast from 'react-hot-toast';
import { useAuth } from '../contexts/AuthContext';

function Users() {
  const { user } = useAuth();
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [editingUser, setEditingUser] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterType, setFilterType] = useState('all');
  const [filterStatus, setFilterStatus] = useState('all');
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  const [showUploadModal, setShowUploadModal] = useState(false);
  const [uploadFile, setUploadFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [uploadResults, setUploadResults] = useState(null);
  const [previewData, setPreviewData] = useState(null);
  const [showPreview, setShowPreview] = useState(false);
  const [showFingerprintModal, setShowFingerprintModal] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [availableFingerprintIds, setAvailableFingerprintIds] = useState([]);
  const [selectedFingerprintId, setSelectedFingerprintId] = useState('');
  const [assigningFingerprint, setAssigningFingerprint] = useState(false);
  const [showRfidModal, setShowRfidModal] = useState(false);
  const [rfidScanMode, setRfidScanMode] = useState(false);
  const [scannedRfidData, setScannedRfidData] = useState('');
  const [manualRfidInput, setManualRfidInput] = useState('');
  const [assigningRfid, setAssigningRfid] = useState(false);

  // Delete confirmation modal states
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [deleteItem, setDeleteItem] = useState(null);
  const [isDeleting, setIsDeleting] = useState(false);
  const [modalAnimation, setModalAnimation] = useState('hidden');

  // Bulk delete states
  const [selectedUsers, setSelectedUsers] = useState(new Set());
  const [selectAll, setSelectAll] = useState(false);
  const [showBulkDeleteModal, setShowBulkDeleteModal] = useState(false);
  const [isBulkDeleting, setIsBulkDeleting] = useState(false);

  // Delete all by type states
  const [showDeleteAllModal, setShowDeleteAllModal] = useState(false);
  const [deleteAllType, setDeleteAllType] = useState(''); // 'students' or 'instructors'
  const [isDeletingAll, setIsDeletingAll] = useState(false);
  const [showSecondConfirmation, setShowSecondConfirmation] = useState(false);
  const [showBulkActions, setShowBulkActions] = useState(false);
  const [showMultiSelect, setShowMultiSelect] = useState(false);

  // Database counts for each user type
  const [databaseCounts, setDatabaseCounts] = useState({
    student: 0,
    instructor: 0,
    admin: 0
  });

  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    user_type: 'student',
    student_id: '',
    faculty_id: '',
    year_level: '',
    department: '',
    status: 'active'
  });

  useEffect(() => {
    fetchUsers();
    fetchDatabaseCounts();
  }, [currentPage, filterType, filterStatus, searchTerm]);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (showBulkActions && !event.target.closest('.bulk-actions-dropdown')) {
        setShowBulkActions(false);
      }
    };

    if (showBulkActions) {
      document.addEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showBulkActions]);

  const fetchUsers = async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams({
        page: currentPage,
        limit: 20
      });

      if (filterType !== 'all') {
        params.append('type', filterType);
      }
      if (filterStatus !== 'all') {
        params.append('status', filterStatus);
      }
      if (searchTerm) {
        params.append('search', searchTerm);
      }

      const response = await axios.get(`http://localhost:5000/api/users?${params}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });

      setUsers(response.data.users);
      setTotalPages(response.data.pagination.pages);
    } catch (error) {
      /* eslint-disable */console.error(...oo_tx(`3003150467_141_6_141_51_11`,'Error fetching users:', error));
      toast.error('Failed to load users');
    } finally {
      setLoading(false);
    }
  };

  const fetchDatabaseCounts = async () => {
    try {
      const [studentsResponse, instructorsResponse] = await Promise.all([
        axios.get('http://localhost:5000/api/users/by-type/student', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        }),
        axios.get('http://localhost:5000/api/users/by-type/instructor', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        })
      ]);

      setDatabaseCounts({
        student: studentsResponse.data.count,
        instructor: instructorsResponse.data.count,
        admin: 0 // We don't need admin count for delete all functionality
      });
    } catch (error) {
      /* eslint-disable */console.error(...oo_tx(`3003150467_165_6_165_61_11`,'Error fetching database counts:', error));
      // Don't show error to user as this is background functionality
    }
  };



  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (editingUser) {
        await axios.put(`http://localhost:5000/api/users/${editingUser.USERID}`, formData, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        toast.success('User updated successfully');
      } else {
        await axios.post('http://localhost:5000/api/users', formData, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        toast.success('User created successfully');
      }

      setShowModal(false);
      setEditingUser(null);
      resetForm();
      fetchUsers();
    } catch (error) {
      /* eslint-disable */console.error(...oo_tx(`3003150467_193_6_193_48_11`,'Error saving user:', error));

      if (error.response?.status === 409) {
        // Handle conflict errors with more specific messages
        const errorMessage = error.response?.data?.message || 'User information conflicts with existing data';
        toast.error(errorMessage);
      } else if (error.response?.status === 400) {
        // Handle validation errors
        const errorMessage = error.response?.data?.message || 'Please check your input data';
        toast.error(errorMessage);
      } else {
        toast.error(error.response?.data?.message || 'Failed to save user');
      }
    }
  };

  const handleEdit = (user) => {
    setEditingUser(user);
    setFormData({
      first_name: user.FIRSTNAME || '',
      last_name: user.LASTNAME || '',
      user_type: user.USERTYPE || 'student',
      student_id: user.STUDENTID || '',
      faculty_id: user.FACULTYID || '',
      year_level: user.YEARLEVEL || '',
      department: user.DEPARTMENT || '',
      status: user.STATUS || 'active'
    });
    setShowModal(true);
  };

  const handleDelete = (user) => {
    const title = 'Delete User';
    const message = `Are you sure you want to delete "${user.FIRSTNAME} ${user.LASTNAME}"? This action cannot be undone and will remove all associated data including attendance records, enrollments, and device assignments.`;
    const confirmText = 'Delete User';

    setDeleteItem({
      ...user,
      title,
      message,
      confirmText
    });
    setShowDeleteModal(true);
    setTimeout(() => setModalAnimation('visible'), 10);
  };

  const confirmDelete = async () => {
    if (!deleteItem) return;

    setIsDeleting(true);

    try {
      await axios.delete(`http://localhost:5000/api/users/${deleteItem.USERID}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });

      // Show enhanced success message
      toast.success('User deleted successfully!', {
        duration: 4000,
        style: {
          background: '#f0fdf4',
          border: '1px solid #bbf7d0',
          color: '#166534',
        },
      });

      // Refresh data
      await fetchUsers();

    } catch (error) {
      /* eslint-disable */console.error(...oo_tx(`3003150467_263_6_263_50_11`,'Error deleting user:', error));
      const errorMessage = error.response?.data?.message || 'Failed to delete user';
      const errorDetails = error.response?.data?.details;

      // Show detailed error message
      if (errorDetails) {
        toast.error(`${errorMessage}: ${errorDetails}`, {
          duration: 8000,
          style: {
            background: '#fef2f2',
            border: '1px solid #fecaca',
            color: '#dc2626',
          },
        });
      } else {
        toast.error(errorMessage);
      }
    } finally {
      setIsDeleting(false);
      setModalAnimation('hidden');
      setTimeout(() => {
        setShowDeleteModal(false);
        setDeleteItem(null);
      }, 300);
    }
  };

  const resetForm = () => {
    setFormData({
      first_name: '',
      last_name: '',
      user_type: 'student',
      student_id: '',
      faculty_id: '',
      year_level: '',
      department: '',
      status: 'active'
    });
  };

  // Bulk delete functions
  const handleSelectUser = (userId) => {
    const newSelectedUsers = new Set(selectedUsers);
    if (newSelectedUsers.has(userId)) {
      newSelectedUsers.delete(userId);
    } else {
      newSelectedUsers.add(userId);
    }
    setSelectedUsers(newSelectedUsers);

    // Update select all state
    setSelectAll(newSelectedUsers.size === users.length && users.length > 0);
  };

  const handleSelectAll = () => {
    if (selectAll) {
      setSelectedUsers(new Set());
    } else {
      const userIds = users.map(user => user.USERID);
      setSelectedUsers(new Set(userIds));
    }
    setSelectAll(!selectAll);
  };

  const toggleMultiSelect = () => {
    if (showMultiSelect) {
      // Clear selections when turning off multi-select
      setSelectedUsers(new Set());
      setSelectAll(false);
    }
    setShowMultiSelect(!showMultiSelect);
  };

  const handleBulkDelete = () => {
    if (selectedUsers.size === 0) return;
    setShowBulkDeleteModal(true);
    setTimeout(() => setModalAnimation('visible'), 10);
  };

  const confirmBulkDelete = async () => {
    if (selectedUsers.size === 0) return;

    setIsBulkDeleting(true);

    try {
      const response = await axios.delete('http://localhost:5000/api/users/bulk-delete', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        data: { ids: Array.from(selectedUsers) }
      });

      // Show success message
      toast.success(`${selectedUsers.size} users deleted successfully!`, {
        duration: 4000,
        style: {
          background: '#f0fdf4',
          border: '1px solid #bbf7d0',
          color: '#166534',
        },
      });

      // Clear selections and refresh data
      setSelectedUsers(new Set());
      setSelectAll(false);
      await fetchUsers();

    } catch (error) {
      /* eslint-disable */console.error(...oo_tx(`3003150467_369_6_369_56_11`,'Error bulk deleting users:', error));
      const errorMessage = error.response?.data?.message || 'Failed to delete users';
      toast.error(errorMessage);
    } finally {
      setIsBulkDeleting(false);
      setModalAnimation('hidden');
      setTimeout(() => {
        setShowBulkDeleteModal(false);
      }, 300);
    }
  };

  // Delete all by type functions
  const handleDeleteAllByType = (type) => {
    setDeleteAllType(type);
    setShowSecondConfirmation(false);
    setShowDeleteAllModal(true);
    setTimeout(() => setModalAnimation('visible'), 10);
  };

  const confirmDeleteAllFirst = () => {
    setShowSecondConfirmation(true);
  };

  const confirmDeleteAllFinal = async () => {
    if (!deleteAllType) return;

    setIsDeletingAll(true);

    try {
      // First, fetch ALL users of the specified type from the database
      const response = await axios.get(`http://localhost:5000/api/users/by-type/${deleteAllType}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });

      const allUsersOfType = response.data.users;
      const userIds = allUsersOfType.map(user => user.USERID);

      if (userIds.length === 0) {
        toast.error(`No ${deleteAllType}s found in the database to delete`);
        return;
      }

      // Now delete all users of this type
      const deleteResponse = await axios.delete('http://localhost:5000/api/users/bulk-delete', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        data: { ids: userIds }
      });

      // Show success message
      toast.success(`All ${userIds.length} ${deleteAllType}s deleted successfully from the database!`, {
        duration: 5000,
        style: {
          background: '#f0fdf4',
          border: '1px solid #bbf7d0',
          color: '#166534',
        },
      });

      // Clear selections and refresh data
      setSelectedUsers(new Set());
      setSelectAll(false);
      await fetchUsers();
      await fetchDatabaseCounts(); // Refresh database counts after deletion

    } catch (error) {
      /* eslint-disable */console.error(...oo_tx(`3003150467_435_6_435_67_11`,`Error deleting all ${deleteAllType}s:`, error));
      const errorMessage = error.response?.data?.message || `Failed to delete all ${deleteAllType}s`;
      toast.error(errorMessage);
    } finally {
      setIsDeletingAll(false);
      setModalAnimation('hidden');
      setTimeout(() => {
        setShowDeleteAllModal(false);
        setShowSecondConfirmation(false);
        setDeleteAllType('');
      }, 300);
    }
  };

  const cancelDeleteAll = () => {
    setModalAnimation('hidden');
    setTimeout(() => {
      setShowDeleteAllModal(false);
      setShowSecondConfirmation(false);
      setDeleteAllType('');
    }, 300);
  };

  const handleFilePreview = async () => {
    if (!uploadFile) {
      toast.error('Please select a file to upload');
      return;
    }

    setUploading(true);
    const formData = new FormData();
    formData.append('excelFile', uploadFile);

    try {
      const response = await axios.post('http://localhost:5000/api/users/preview-excel', formData, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'multipart/form-data'
        }
      });

      setPreviewData(response.data);
      setShowPreview(true);
      toast.success(`Preview generated for ${response.data.summary.previewRows} users`);

    } catch (error) {
      /* eslint-disable */console.error(...oo_tx(`3003150467_481_6_481_44_11`,'Preview error:', error));
      toast.error(error.response?.data?.message || 'Failed to preview Excel file');

      if (error.response?.data) {
        setPreviewData(error.response.data);
        setShowPreview(true);
      }
    } finally {
      setUploading(false);
    }
  };

  const handleConfirmUpload = async () => {
    if (!uploadFile) {
      toast.error('Please select a file to upload');
      return;
    }

    setUploading(true);
    const formData = new FormData();
    formData.append('excelFile', uploadFile);

    try {
      const response = await axios.post('http://localhost:5000/api/users/upload-excel', formData, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'multipart/form-data'
        }
      });

      setUploadResults(response.data);
      setShowPreview(false);
      setPreviewData(null);
      toast.success(`Successfully processed ${response.data.summary.insertedUsers} users from Excel file`);

      // Refresh the users list
      fetchUsers();

      // Reset upload state
      setUploadFile(null);

    } catch (error) {
      /* eslint-disable */console.error(...oo_tx(`3003150467_523_6_523_43_11`,'Upload error:', error));
      toast.error(error.response?.data?.message || 'Failed to upload Excel file');

      if (error.response?.data?.errors) {
        setUploadResults(error.response.data);
      }
    } finally {
      setUploading(false);
    }
  };

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      // Validate file type
      const allowedTypes = ['.xlsx', '.xls', '.csv'];
      const fileExtension = file.name.toLowerCase().substring(file.name.lastIndexOf('.'));

      if (!allowedTypes.includes(fileExtension)) {
        toast.error('Please select a valid Excel file (.xlsx, .xls) or CSV file');
        return;
      }

      // Validate file size (10MB limit)
      if (file.size > 10 * 1024 * 1024) {
        toast.error('File size must be less than 10MB');
        return;
      }

      setUploadFile(file);
      setUploadResults(null);
    }
  };

  const fetchAvailableFingerprintIds = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/users/fingerprint-ids/available', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setAvailableFingerprintIds(response.data.all_ids); // Use all_ids instead of available_ids
    } catch (error) {
      /* eslint-disable */console.error(...oo_tx(`3003150467_564_6_564_71_11`,'Error fetching available fingerprint IDs:', error));
      toast.error('Failed to load available fingerprint IDs');
    }
  };

  const handleAssignFingerprint = async (user) => {
    setSelectedUser(user);
    setSelectedFingerprintId('');
    await fetchAvailableFingerprintIds();
    setShowFingerprintModal(true);
  };

  const handleFingerprintAssignment = async () => {
    if (!selectedFingerprintId) {
      toast.error('Please select a fingerprint ID');
      return;
    }

    try {
      setAssigningFingerprint(true);

      const response = await axios.post(
        `http://localhost:5000/api/users/${selectedUser.USERID}/assign-fingerprint`,
        { fingerprint_id: parseInt(selectedFingerprintId) },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        }
      );

      toast.success(
        <div>
          <div className="font-medium"> Fingerprint Assigned!</div>
          <div className="text-sm mt-1">
            <div><strong>{response.data.user.name}</strong></div>
            <div>Fingerprint ID: {response.data.user.fingerprint_id}</div>
            <div>Student ID: {response.data.user.student_id}</div>
          </div>
        </div>,
        { duration: 4000 }
      );

      setShowFingerprintModal(false);
      setSelectedUser(null);
      setSelectedFingerprintId('');
      fetchUsers(); // Refresh the users list

    } catch (error) {
      /* eslint-disable */console.error(...oo_tx(`3003150467_611_6_611_58_11`,'Error assigning fingerprint:', error));
      toast.error(error.response?.data?.message || 'Failed to assign fingerprint');
    } finally {
      setAssigningFingerprint(false);
    }
  };

  // RFID Assignment Functions
  const handleOpenRfidModal = (userData) => {
    setSelectedUser(userData);
    setShowRfidModal(true);
    setRfidScanMode(false);
    setScannedRfidData('');
    setManualRfidInput(userData.RFIDTAG || '');
  };

  const handleStartRfidScan = () => {
    setRfidScanMode(true);
    setScannedRfidData('');
    toast.success('Scan mode activated! Scan an RFID card now...');

    // Focus on a hidden input to capture RFID data
    const hiddenInput = document.getElementById('rfid-scan-input');
    if (hiddenInput) {
      hiddenInput.focus();
    }
  };

  const handleRfidScanInput = (e) => {
    if (e.key === 'Enter' && rfidScanMode) {
      const rfidData = e.target.value.trim();
      if (rfidData.length > 0) {
        setScannedRfidData(rfidData);
        setManualRfidInput(rfidData);
        setRfidScanMode(false);
        toast.success(`RFID captured: ${rfidData}`);
        e.target.value = '';
      }
    }
  };

  const handleAssignRfid = async () => {
    const rfidToAssign = scannedRfidData || manualRfidInput;

    if (!selectedUser || !rfidToAssign.trim()) {
      toast.error('Please provide an RFID code');
      return;
    }

    try {
      setAssigningRfid(true);

      const response = await axios.put(
        `http://localhost:5000/api/users/${selectedUser.USERID}/assign-rfid`,
        { rfid_tag: rfidToAssign.trim() },
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );

      if (response.status === 200) {
        toast.success('RFID assigned successfully!');
        setShowRfidModal(false);
        setSelectedUser(null);
        setScannedRfidData('');
        setManualRfidInput('');
        fetchUsers(); // Refresh the users list
      }
    } catch (error) {
      /* eslint-disable */console.error(...oo_tx(`3003150467_678_6_678_51_11`,'Error assigning RFID:', error));
      if (error.response?.status === 400) {
        toast.error(error.response.data.message || 'Invalid RFID data');
      } else if (error.response?.status === 409) {
        toast.error(error.response.data.message || 'RFID tag already assigned to another user');
      } else {
        toast.error('Failed to assign RFID');
      }
    } finally {
      setAssigningRfid(false);
    }
  };

  const resetUploadModal = () => {
    setShowUploadModal(false);
    setUploadFile(null);
    setUploadResults(null);
    setPreviewData(null);
    setShowPreview(false);
    setUploading(false);
  };

  const getUserTypeIcon = (type) => {
    switch (type) {
      case 'admin':
        return <UserIcon className="h-5 w-5 text-red-600" />;
      case 'instructor':
        return <AcademicCapIcon className="h-5 w-5 text-blue-600" />;
      case 'student':
        return <UserGroupIcon className="h-5 w-5 text-green-600" />;
      case 'custodian':
        return <UserIcon className="h-5 w-5 text-orange-600" />;
      case 'dean':
        return <AcademicCapIcon className="h-5 w-5 text-purple-600" />;
      default:
        return <UserIcon className="h-5 w-5 text-gray-600" />;
    }
  };

  const getUserTypeColor = (type) => {
    switch (type) {
      case 'admin':
        return 'bg-red-100 text-red-800';
      case 'instructor':
        return 'bg-blue-100 text-blue-800';
      case 'student':
        return 'bg-green-100 text-green-800';
      case 'custodian':
        return 'bg-orange-100 text-orange-800';
      case 'dean':
        return 'bg-purple-100 text-purple-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusColor = (status) => {
    return status === 'active' 
      ? 'bg-green-100 text-green-800' 
      : 'bg-red-100 text-red-800';
  };

  if (loading && users.length === 0) {
    return (
      <div className="flex items-center justify-center h-64">
