import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import axios from 'axios';
import toast from 'react-hot-toast';
import {
  CpuChipIcon,
  FingerPrintIcon,
  KeyIcon,
  PlayIcon
} from '@heroicons/react/24/outline';

// Set up axios defaults
axios.defaults.baseURL = 'http://localhost:5000';

function TestPage() {
  const [selectedRoom, setSelectedRoom] = useState('');
  const [selectedUser, setSelectedUser] = useState('');
  const [selectedSubject, setSelectedSubject] = useState('');
  const [scanType, setScanType] = useState('RFID');
  const [location, setLocation] = useState('outside');
  const [fingerprintId, setFingerprintId] = useState('');
  const [testingFingerprint, setTestingFingerprint] = useState(false);
  const [enrollmentStatus, setEnrollmentStatus] = useState(null);
  const [fingerprintUser, setFingerprintUser] = useState(null);
  const [checkingFingerprint, setCheckingFingerprint] = useState(false);
  const queryClient = useQueryClient();

  // Fetch rooms
  const { data: rooms } = useQuery('rooms', async () => {
    const response = await axios.get('/api/rooms');
    return response.data.rooms;
  });

  // Fetch users
  const { data: users } = useQuery('users', async () => {
    const response = await axios.get('/api/users');
    return response.data.users;
  });

  // Fetch subjects
  const { data: subjects } = useQuery('subjects', async () => {
    const response = await axios.get('/api/subjects');
    return response.data.subjects;
  });

  // Check enrollment status when student and subject are selected
  const checkEnrollmentStatus = async (userId, subjectId) => {
    if (!userId || !subjectId) {
      setEnrollmentStatus(null);
      return;
    }

    try {
      // Get user type first
      const user = users?.find(u => u.USERID === userId);
      if (user?.USERTYPE !== 'student') {
        setEnrollmentStatus(null);
        return;
      }

      console.log('Checking enrollment for:', { userId, subjectId, userType: user.USERTYPE });

      // Check if student is enrolled in the subject
      const response = await axios.get(`/api/enrollment/schedule/${subjectId}/check/${userId}`);
      console.log('Enrollment check response:', response.data);
      setEnrollmentStatus(response.data.enrolled);
    } catch (error) {
      console.error('Error checking enrollment:', error);
      console.error('Error details:', error.response?.data);
      setEnrollmentStatus(false);
    }
  };

  // Check fingerprint user and enrollment status
  const checkFingerprintUser = async (fingerprintId) => {
    if (!fingerprintId) {
      setFingerprintUser(null);
      setEnrollmentStatus(null);
      return;
    }

    setCheckingFingerprint(true);
    try {
      // Get user by fingerprint ID
      const response = await axios.get(`/api/users/fingerprint/${fingerprintId}`);
      const user = response.data.user;

      if (user) {
        setFingerprintUser(user);
        console.log('Fingerprint user found:', user);

        // If subject is selected, check enrollment
        if (selectedSubject && user.USERTYPE === 'student') {
          await checkEnrollmentStatus(user.USERID, selectedSubject);
        } else {
          setEnrollmentStatus(null);
        }
      } else {
        setFingerprintUser(null);
        setEnrollmentStatus(null);
      }
    } catch (error) {
      console.error('Error checking fingerprint user:', error);
      setFingerprintUser(null);
      setEnrollmentStatus(null);
    } finally {
      setCheckingFingerprint(false);
    }
  };

  // Simulate scan mutation
  const simulateScan = useMutation(
    async (scanData) => {
      const response = await axios.post('/api/scan/simulate', scanData);
      return response.data;
    },
    {
      onSuccess: (data) => {
        toast.success(data.message);
        queryClient.invalidateQueries(['sessions']);
        queryClient.invalidateQueries(['dashboard-stats']);
      },
      onError: (error) => {
        const errorMessage = error.response?.data?.message || 'Scan simulation failed';

        // Special handling for enrollment error
        if (error.response?.status === 403 && errorMessage.includes('not enrolled')) {
          toast.error(
            <div>
              <div className="font-medium">❌ Student Not Enrolled</div>
              <div className="text-sm mt-1">
                The selected student is not enrolled in this subject.
                <br />
                Please use the Schedules page to enroll the student first.
              </div>
            </div>,
            { duration: 6000 }
          );
        } else {
          toast.error(errorMessage);
        }
      }
    }
  );

  // Test fingerprint attendance
  const handleTestFingerprint = async () => {
    if (!fingerprintId) {
      toast.error('Please enter a fingerprint ID');
      return;
    }

    setTestingFingerprint(true);
    try {
      const requestData = {
        fingerprint_id: parseInt(fingerprintId),
        location: location
      };

      // Only add room_id and subject_id if they are selected (not empty strings)
      if (selectedRoom) {
        requestData.room_id = selectedRoom;
      }
      if (selectedSubject) {
        requestData.subject_id = selectedSubject;
      }

      console.log('Sending fingerprint attendance request:', requestData);

      const response = await axios.post('http://localhost:5000/api/logs/attendance-logs', requestData);

      toast.success(
        <div>
          <div className="font-medium">✅ Fingerprint Attendance Recorded!</div>
          <div className="text-sm mt-1">
            <div><strong>{response.data.attendance.user.name}</strong></div>
            <div>Status: <span className={`font-medium ${
              response.data.attendance.status === 'Present' ? 'text-green-600' :
              response.data.attendance.status === 'Late' ? 'text-yellow-600' : 'text-red-600'
            }`}>{response.data.attendance.status}</span></div>
            <div>Room: {response.data.attendance.room}</div>
            <div>Subject: {response.data.attendance.subject}</div>
          </div>
        </div>,
        { duration: 5000 }
      );
    } catch (error) {
      console.error('Fingerprint test error:', error);
      const errorMessage = error.response?.data?.message || 'Failed to record fingerprint attendance';

      // Special handling for enrollment error
      if (error.response?.status === 403 && errorMessage.includes('not enrolled')) {
        toast.error(
          <div>
            <div className="font-medium">❌ User Not Enrolled</div>
            <div className="text-sm mt-1">
              The fingerprint user is not enrolled in this subject.
              <br />
              Please use the Schedules page to enroll the user first.
            </div>
          </div>,
          { duration: 6000 }
        );
      } else {
        toast.error(errorMessage);
      }
    } finally {
      setTestingFingerprint(false);
    }
  };

  const handleSimulateScan = () => {
    if (!selectedRoom || !selectedUser || !selectedSubject) {
      toast.error('Please select room, user, and subject');
      return;
    }

    simulateScan.mutate({
      user_id: selectedUser,
      room_id: selectedRoom,
      subject_id: selectedSubject,
      auth_method: scanType.toLowerCase(),
      location: location
    });
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-2xl font-bold text-gray-900">IoT Device Simulation</h1>
        <p className="text-gray-600">Test the attendance and access control system</p>
      </div>

      {/* Simulation Panel */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <div className="card">
          <h3 className="text-lg font-medium text-gray-900 mb-4 flex items-center">
            <CpuChipIcon className="h-5 w-5 mr-2" />
            Device Simulation
          </h3>

          <div className="space-y-4">
            {/* Room Selection */}
            <div>
              <label className="label">Select Room</label>
              <select
                value={selectedRoom}
                onChange={(e) => setSelectedRoom(e.target.value)}
                className="input"
              >
                <option value="">Choose a room...</option>
                {rooms?.map((room) => (
                  <option key={room.ROOMID} value={room.ROOMID}>
                    {room.ROOMNUMBER} - {room.BUILDING}
                  </option>
                ))}
              </select>
            </div>

            {/* User Selection */}
            <div>
              <label className="label">Select User</label>
              <select
                value={selectedUser}
                onChange={(e) => {
                  setSelectedUser(e.target.value);
                  if (e.target.value && selectedSubject) {
                    checkEnrollmentStatus(e.target.value, selectedSubject);
                  } else {
                    setEnrollmentStatus(null);
                  }
                }}
                className="input"
              >
                <option value="">Choose a user...</option>
                {users?.map((user) => (
                  <option key={user.USERID} value={user.USERID}>
                    {user.FIRSTNAME} {user.LASTNAME} ({user.USERTYPE})
                  </option>
                ))}
              </select>
            </div>

            {/* Subject Selection */}
            <div>
              <label className="label">Select Subject</label>
              <select
                value={selectedSubject}
                onChange={(e) => {
                  setSelectedSubject(e.target.value);
                  if (selectedUser && e.target.value) {
                    checkEnrollmentStatus(selectedUser, e.target.value);
                  } else {
                    setEnrollmentStatus(null);
                  }
                }}
                className="input"
              >
                <option value="">Choose a subject...</option>
                {subjects?.map((subject) => (
                  <option key={subject.SUBJECTID} value={subject.SUBJECTID}>
                    {subject.SUBJECTCODE} - {subject.SUBJECTNAME}
                  </option>
                ))}
              </select>
            </div>

            {/* Enrollment Status Indicator */}
            {enrollmentStatus !== null && (
              <div className={`p-3 rounded-lg border ${
                enrollmentStatus 
                  ? 'bg-green-50 border-green-200 text-green-800' 
                  : 'bg-red-50 border-red-200 text-red-800'
              }`}>
                <div className="flex items-center">
                  <div className={`h-2 w-2 rounded-full mr-3 ${
                    enrollmentStatus ? 'bg-green-400' : 'bg-red-400'
                  }`}></div>
                  <span className="text-sm font-medium">
                    {enrollmentStatus 
                      ? '✅ Student is enrolled in this subject' 
                      : '❌ Student is not enrolled in this subject'
                    }
                  </span>
                </div>
                {!enrollmentStatus && (
                  <div className="text-xs mt-1">
                    Use the Schedules page to enroll the student first.
                  </div>
                )}
              </div>
            )}

            {/* Scan Type */}
            <div>
              <label className="label">Authentication Method</label>
              <div className="flex space-x-4">
                <label className="flex items-center">
                  <input
                    type="radio"
                    value="RFID"
                    checked={scanType === 'RFID'}
                    onChange={(e) => setScanType(e.target.value)}
                    className="mr-2"
                  />
                  <KeyIcon className="h-4 w-4 mr-1" />
                  RFID
                </label>
                <label className="flex items-center">
                  <input
                    type="radio"
                    value="Fingerprint"
                    checked={scanType === 'Fingerprint'}
                    onChange={(e) => setScanType(e.target.value)}
                    className="mr-2"
                  />
                  <FingerPrintIcon className="h-4 w-4 mr-1" />
                  Fingerprint
                </label>
              </div>
            </div>

            {/* Location */}
            <div>
              <label className="label">Scan Location</label>
              <div className="flex space-x-4">
                <label className="flex items-center">
                  <input
                    type="radio"
                    value="outside"
                    checked={location === 'outside'}
                    onChange={(e) => setLocation(e.target.value)}
                    className="mr-2"
                  />
                  Outside Room
                </label>
                <label className="flex items-center">
                  <input
                    type="radio"
                    value="inside"
                    checked={location === 'inside'}
                    onChange={(e) => setLocation(e.target.value)}
                    className="mr-2"
                  />
                  Inside Room
                </label>
              </div>
            </div>

            {/* Simulate Button */}
            <button
              onClick={handleSimulateScan}
              disabled={simulateScan.isLoading || (enrollmentStatus === false)}
              className="btn-primary w-full"
            >
              {simulateScan.isLoading ? (
                <div className="flex items-center justify-center">
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Simulating...
                </div>
              ) : enrollmentStatus === false ? (
                <div className="flex items-center justify-center">
                  <div className="h-4 w-4 mr-2">❌</div>
                  Student Not Enrolled
                </div>
              ) : (
                <div className="flex items-center justify-center">
                  <PlayIcon className="h-4 w-4 mr-2" />
                  Simulate Scan
                </div>
              )}
            </button>
          </div>
        </div>

        {/* Fingerprint Test */}
        <div className="card">
          <h3 className="text-lg font-medium text-gray-900 mb-4 flex items-center">
            <FingerPrintIcon className="h-5 w-5 mr-2 text-purple-600" />
            Test Fingerprint Attendance
          </h3>

          <div className="space-y-4">
            {/* Instructions */}
            <div className="bg-purple-50 border border-purple-200 rounded-lg p-4">
              <h4 className="font-medium text-purple-800 mb-2">Instructions:</h4>
              <ol className="text-sm text-purple-700 space-y-1 list-decimal list-inside">
                <li>Enter a fingerprint ID (1-127) that has been assigned to a user</li>
                <li>Select location (inside/outside)</li>
                <li>Optionally select a room and subject</li>
                <li>Click "Test Fingerprint" to simulate ESP32 sending data</li>
              </ol>
            </div>

            {/* Fingerprint ID Input */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Fingerprint ID (1-127)
              </label>
              <input
                type="number"
                min="1"
                max="127"
                value={fingerprintId}
                onChange={(e) => {
                  setFingerprintId(e.target.value);
                  if (e.target.value) {
                    checkFingerprintUser(e.target.value);
                  } else {
                    setFingerprintUser(null);
                    setEnrollmentStatus(null);
                  }
                }}
                placeholder="Enter fingerprint ID..."
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
              />
            </div>

            {/* Room Selection */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Room (Optional)
              </label>
              <select
                value={selectedRoom}
                onChange={(e) => setSelectedRoom(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
              >
                <option value="">Auto-detect room</option>
                {rooms?.map((room) => (
                  <option key={room.ROOMID} value={room.ROOMID}>
                    {room.ROOMNUMBER} - {room.ROOMNAME}
                  </option>
                ))}
              </select>
            </div>

            {/* Subject Selection */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Subject (Optional)
              </label>
              <select
                value={selectedSubject}
                onChange={(e) => {
                  setSelectedSubject(e.target.value);
                  // If fingerprint user is found and subject is selected, check enrollment
                  if (fingerprintUser && e.target.value && fingerprintUser.USERTYPE === 'student') {
                    checkEnrollmentStatus(fingerprintUser.USERID, e.target.value);
                  } else {
                    setEnrollmentStatus(null);
                  }
                }}
                className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
              >
                <option value="">Auto-detect subject</option>
                {subjects?.map((subject) => (
                  <option key={subject.SUBJECTID} value={subject.SUBJECTID}>
                    {subject.SUBJECTCODE} - {subject.SUBJECTNAME}
                  </option>
                ))}
              </select>
            </div>

            {/* Fingerprint User Information */}
            {checkingFingerprint && (
              <div className="bg-blue-50 border border-blue-200 rounded-lg p-3">
                <div className="flex items-center">
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-600 mr-3"></div>
                  <span className="text-sm text-blue-800">Looking up fingerprint user...</span>
                </div>
              </div>
            )}

            {fingerprintUser && (
              <div className="bg-green-50 border border-green-200 rounded-lg p-3">
                <div className="flex items-center">
                  <div className="h-2 w-2 bg-green-400 rounded-full mr-3"></div>
                  <span className="text-sm font-medium text-green-800">
                    ✅ Fingerprint User Found
                  </span>
                </div>
                <div className="text-sm text-green-700 mt-1">
                  <div><strong>Name:</strong> {fingerprintUser.FIRSTNAME} {fingerprintUser.LASTNAME}</div>
                  <div><strong>Type:</strong> {fingerprintUser.USERTYPE}</div>
                  <div><strong>Status:</strong> {fingerprintUser.STATUS}</div>
                  {fingerprintUser.STUDENTID && <div><strong>Student ID:</strong> {fingerprintUser.STUDENTID}</div>}
                  {fingerprintUser.EMPLOYEEID && <div><strong>Employee ID:</strong> {fingerprintUser.EMPLOYEEID}</div>}
                </div>
              </div>
            )}

            {/* Enrollment Status for Fingerprint User */}
            {fingerprintUser && selectedSubject && enrollmentStatus !== null && (
              <div className={`p-3 rounded-lg border ${
                enrollmentStatus 
                  ? 'bg-green-50 border-green-200 text-green-800' 
                  : 'bg-red-50 border-red-200 text-red-800'
              }`}>
                <div className="flex items-center">
                  <div className={`h-2 w-2 rounded-full mr-3 ${
                    enrollmentStatus ? 'bg-green-400' : 'bg-red-400'
                  }`}></div>
                  <span className="text-sm font-medium">
                    {enrollmentStatus 
                      ? '✅ User is enrolled in this subject' 
                      : '❌ User is not enrolled in this subject'
                    }
                  </span>
                </div>
                {!enrollmentStatus && (
                  <div className="text-xs mt-1">
                    Use the Schedules page to enroll the user first.
                  </div>
                )}
              </div>
            )}

            {/* Location Selection */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Location
              </label>
              <div className="flex space-x-4">
                <label className="flex items-center">
                  <input
                    type="radio"
                    name="fingerprint-location"
                    value="outside"
                    checked={location === 'outside'}
                    onChange={(e) => setLocation(e.target.value)}
                    className="mr-2"
                  />
                  Outside Room
                </label>
                <label className="flex items-center">
                  <input
                    type="radio"
                    name="fingerprint-location"
                    value="inside"
                    checked={location === 'inside'}
                    onChange={(e) => setLocation(e.target.value)}
                    className="mr-2"
                  />
                  Inside Room
                </label>
              </div>
            </div>

            {/* Test Button */}
            <button
              onClick={handleTestFingerprint}
              disabled={testingFingerprint || !fingerprintId || (enrollmentStatus === false)}
              className="w-full px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-purple-600 hover:bg-purple-700 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {testingFingerprint ? (
                <div className="flex items-center justify-center">
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Testing Fingerprint...
                </div>
              ) : enrollmentStatus === false ? (
                <div className="flex items-center justify-center">
                  <div className="h-4 w-4 mr-2">❌</div>
                  User Not Enrolled
                </div>
              ) : (
                <div className="flex items-center justify-center">
                  <FingerPrintIcon className="h-4 w-4 mr-2" />
                  Test Fingerprint
                </div>
              )}
            </button>
          </div>
        </div>

        {/* Device Status */}
        <div className="card">
          <h3 className="text-lg font-medium text-gray-900 mb-4">Device Status</h3>

          <div className="space-y-4">
            <div className="bg-green-50 border border-green-200 rounded-lg p-4">
              <div className="flex items-center">
                <div className="h-2 w-2 bg-green-400 rounded-full mr-3"></div>
                <span className="text-sm font-medium text-green-800">RFID Readers</span>
                <span className="ml-auto text-green-600">Online (Simulated)</span>
              </div>
            </div>

            <div className="bg-green-50 border border-green-200 rounded-lg p-4">
              <div className="flex items-center">
                <div className="h-2 w-2 bg-green-400 rounded-full mr-3"></div>
                <span className="text-sm font-medium text-green-800">Fingerprint Scanners</span>
                <span className="ml-auto text-green-600">Online (Simulated)</span>
              </div>
            </div>

            <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
              <div className="flex items-center">
                <div className="h-2 w-2 bg-yellow-400 rounded-full mr-3"></div>
                <span className="text-sm font-medium text-yellow-800">Door Controllers</span>
                <span className="ml-auto text-yellow-600">Simulated</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Instructions */}
      <div className="card">
        <h3 className="text-lg font-medium text-gray-900 mb-4">Testing Instructions</h3>
        <div className="prose prose-sm text-gray-600">
          <h4>Instructor Workflow:</h4>
          <ol>
            <li><strong>Outside Scan:</strong> Select instructor, room, subject, and "Outside" location to start/end sessions and control door</li>
            <li><strong>Inside Scan:</strong> Select instructor, room, subject, and "Inside" location to end session only</li>
          </ol>

          <h4>Student Workflow:</h4>
          <ol>
            <li><strong>Inside Scan:</strong> Select student, room, subject, and "Inside" location to record attendance</li>
            <li>Students can only scan inside rooms during active sessions</li>
            <li><strong>Important:</strong> Students must be enrolled in the subject before they can record attendance</li>
            <li>Use the Schedules page to enroll students in subjects if needed</li>
          </ol>

          <h4>System Behavior:</h4>
          <ul>
            <li>Door unlocks when instructor starts session (outside scan)</li>
            <li>Door locks when instructor ends session (outside scan)</li>
            <li>Student attendance is recorded with late detection</li>
            <li>All scans are logged for audit purposes</li>
          </ul>
        </div>
      </div>

      {/* Recent Test Results */}
      <div className="card">
        <h3 className="text-lg font-medium text-gray-900 mb-4">Recent Test Results</h3>
        <div className="text-sm text-gray-500">
          Check the Access Logs and Attendance Logs pages to see the results of your simulations.
        </div>
      </div>
    </div>
  );
}

export default TestPage;
