import React, { useState, useEffect } from 'react';
import { 
  PlayIcon, 
  StopIcon, 
  FingerPrintIcon,
  IdentificationIcon,
  CheckCircleIcon,
  XCircleIcon,
  ExclamationTriangleIcon,
  LockClosedIcon,
  LockOpenIcon
} from '@heroicons/react/24/outline';
import axios from 'axios';
import toast from 'react-hot-toast';

function Sessions() {
  const [sessions, setSessions] = useState([]);
  const [allSchedules, setAllSchedules] = useState([]);
  const [todaysSchedule, setTodaysSchedule] = useState([]);
  const [instructors, setInstructors] = useState([]);
  const [rooms, setRooms] = useState([]);
  const [selectedInstructor, setSelectedInstructor] = useState('');
  const [selectedRoom, setSelectedRoom] = useState('');
  const [selectedLocation, setSelectedLocation] = useState('outside');
  const [loading, setLoading] = useState(true);
  const [showScanModal, setShowScanModal] = useState(false);
  const [scanType, setScanType] = useState('fingerprint'); // 'fingerprint' or 'rfid'

  useEffect(() => {
    fetchData();
    // Set up real-time updates
    const interval = setInterval(fetchData, 30000); // Update every 30 seconds
    return () => clearInterval(interval);
  }, []);

  const fetchData = async () => {
    try {
      const token = localStorage.getItem('token');
      const headers = { Authorization: `Bearer ${token}` };

      // Fetch instructors
      const instructorsResponse = await axios.get('http://localhost:5000/api/users?type=instructor', { headers });
      setInstructors(instructorsResponse.data.users || []);

      // Fetch rooms
      const roomsResponse = await axios.get('http://localhost:5000/api/rooms', { headers });
      setRooms(roomsResponse.data.rooms || []);

      // Fetch all schedules
      const schedulesResponse = await axios.get('http://localhost:5000/api/schedules', { headers });
      const allSchedulesData = schedulesResponse.data.schedules || [];
      setAllSchedules(allSchedulesData);

      // Filter today's schedules
      const today = new Date().toISOString().split('T')[0];
      const currentDay = new Date().toLocaleDateString('en-US', { weekday: 'long' });
      const todaysSchedules = allSchedulesData.filter(schedule => schedule.DAYOFWEEK === currentDay);
      setTodaysSchedule(todaysSchedules);

      // Fetch active sessions
      const sessionsResponse = await axios.get('http://localhost:5000/api/sessions/active', { headers });
      setSessions(sessionsResponse.data || []);

    } catch (error) {
      console.error('Error fetching data:', error);
      toast.error('Failed to load session data');

      // Fallback to mock data if API fails
      const mockInstructors = [
        { USERID: '1', FIRSTNAME: 'John', LASTNAME: 'Smith', EMPLOYEEID: 'EMP001' },
        { USERID: '2', FIRSTNAME: 'Jane', LASTNAME: 'Doe', EMPLOYEEID: 'EMP002' },
        { USERID: '3', FIRSTNAME: 'Bob', LASTNAME: 'Johnson', EMPLOYEEID: 'EMP003' }
      ];

      const mockRooms = [
        { ROOMID: '1', ROOMNUMBER: 'A101', ROOMNAME: 'Computer Lab 1', BUILDING: 'Main Building', DOORSTATUS: 'locked' },
        { ROOMID: '2', ROOMNUMBER: 'B201', ROOMNAME: 'Mathematics Room', BUILDING: 'Academic Building', DOORSTATUS: 'locked' },
        { ROOMID: '3', ROOMNUMBER: 'C301', ROOMNAME: 'English Classroom', BUILDING: 'Liberal Arts Building', DOORSTATUS: 'locked' }
      ];

      const currentDay = new Date().toLocaleDateString('en-US', { weekday: 'long' });
      const mockTodaysSchedule = [
        {
          SCHEDULEID: '1',
          SUBJECTID: '1',
          SUBJECTCODE: 'CS101',
          SUBJECTNAME: 'Introduction to Computer Science',
          INSTRUCTORID: '1',
          instructor_name: 'John Smith',
          ROOMID: '1',
          ROOMNUMBER: 'A101',
          ROOMNAME: 'Computer Lab 1',
          DAYOFWEEK: currentDay,
          STARTTIME: '08:00:00',
          ENDTIME: '10:00:00',
          ACADEMICYEAR: '2024-2025',
          SEMESTER: 'First Semester'
        }
      ];

      setInstructors(mockInstructors);
      setRooms(mockRooms);
      setTodaysSchedule(mockTodaysSchedule);
    } finally {
      setLoading(false);
    }
  };

  const handleScan = async (authMethod) => {
    if (!selectedInstructor || !selectedRoom) {
      toast.error('Please select both instructor and room');
      return;
    }

    try {
      const instructor = instructors.find(i => i.USERID === selectedInstructor);
      const room = rooms.find(r => r.ROOMID === selectedRoom);
      const today = new Date().toISOString().split('T')[0];

      // Find matching schedule for today
      const matchingSchedule = todaysSchedule.find(schedule => 
        schedule.INSTRUCTORID === selectedInstructor && 
        schedule.ROOMID === selectedRoom
      );

      if (!matchingSchedule) {
        toast.error('No scheduled class found for this instructor and room today');
        return;
      }

      // Check if there's an active session
      const activeSession = sessions.find(session => 
        session.instructor_id === selectedInstructor && 
        session.room_id === selectedRoom && 
        session.status === 'active' &&
        session.session_date === today
      );

      const token = localStorage.getItem('token');
      const headers = { Authorization: `Bearer ${token}` };

      if (activeSession) {
        // End the session
        await axios.put(`http://localhost:5000/api/sessions/${activeSession.id}/end`, {
          location: selectedLocation,
          auth_method: authMethod.toLowerCase()
        }, { headers });

        toast.success(`ðŸšª Session ended! ${authMethod} scan successful by ${instructor.FIRSTNAME} ${instructor.LASTNAME}`);
      } else {
        // Start new session
        await axios.post('http://localhost:5000/api/sessions', {
          schedule_id: matchingSchedule.SCHEDULEID,
          instructor_id: selectedInstructor,
          room_id: selectedRoom,
          location: selectedLocation,
          auth_method: authMethod.toLowerCase()
        }, { headers });

        toast.success(`ðŸšª Session started! ${authMethod} scan successful by ${instructor.FIRSTNAME} ${instructor.LASTNAME}`);
      }

      // Refresh data
      fetchData();
    } catch (error) {
      console.error('Error processing scan:', error);
      toast.error(error.response?.data?.message || 'Failed to process scan');
    }
  };

  const formatTime = (timeString) => {
    if (!timeString) return '';
    const date = new Date(timeString);
    return date.toLocaleTimeString('en-US', { 
      hour: '2-digit', 
      minute: '2-digit',
      hour12: true 
    });
  };

  const formatScheduleTime = (time) => {
    if (!time) return '';
    const [hours, minutes] = time.split(':');
    const hour = parseInt(hours);
    const ampm = hour >= 12 ? 'PM' : 'AM';
    const displayHour = hour % 12 || 12;
    return `${displayHour}:${minutes} ${ampm}`;
  };

  const getSessionDuration = (startTime, endTime) => {
    if (!startTime) return '';
    const start = new Date(startTime);
    const end = endTime ? new Date(endTime) : new Date();
    const duration = Math.floor((end - start) / (1000 * 60)); // minutes
    const hours = Math.floor(duration / 60);
    const mins = duration % 60;
    return hours > 0 ? `${hours}h ${mins}m` : `${mins}m`;
  };

  const groupSchedulesByDay = (schedules) => {
    const dayOrder = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];
    const grouped = {};

    schedules.forEach(schedule => {
      const day = schedule.DAYOFWEEK;
      if (!grouped[day]) {
        grouped[day] = [];
      }
      grouped[day].push(schedule);
    });

    // Sort schedules within each day by start time
    Object.keys(grouped).forEach(day => {
      grouped[day].sort((a, b) => a.STARTTIME.localeCompare(b.STARTTIME));
    });

    // Return as array sorted by day order
    return dayOrder.map(day => ({
      day,
      schedules: grouped[day] || []
    })).filter(dayGroup => dayGroup.schedules.length > 0);
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Session Management</h1>
          <p className="text-gray-600">Simulate RFID/Fingerprint scans to control class sessions</p>
        </div>
        <div className="flex space-x-3">
          <button
            onClick={() => {
              setScanType('fingerprint');
              setShowScanModal(true);
            }}
            className="inline-flex items-center px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700"
          >
            <FingerPrintIcon className="h-4 w-4 mr-2" />
            Simulate Fingerprint
          </button>
          <button
            onClick={() => {
              setScanType('rfid');
              setShowScanModal(true);
            }}
            className="inline-flex items-center px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-green-600 hover:bg-green-700"
          >
            <IdentificationIcon className="h-4 w-4 mr-2" />
            Simulate RFID
          </button>
        </div>
      </div>

      {/* Simulation Controls */}
      <div className="bg-white rounded-lg shadow p-6">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">Scan Simulation</h2>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Instructor</label>
            <select
              value={selectedInstructor}
              onChange={(e) => setSelectedInstructor(e.target.value)}
              className="w-full border border-gray-300 rounded-md px-3 py-2"
            >
              <option value="">Select Instructor</option>
              {instructors.map(instructor => (
                <option key={instructor.USERID} value={instructor.USERID}>
                  {instructor.FIRSTNAME} {instructor.LASTNAME} ({instructor.EMPLOYEEID})
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Room</label>
            <select
              value={selectedRoom}
              onChange={(e) => setSelectedRoom(e.target.value)}
              className="w-full border border-gray-300 rounded-md px-3 py-2"
            >
              <option value="">Select Room</option>
              {rooms.map(room => (
                <option key={room.ROOMID} value={room.ROOMID}>
                  {room.ROOMNUMBER} - {room.ROOMNAME}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Scanner Location</label>
            <select
              value={selectedLocation}
              onChange={(e) => setSelectedLocation(e.target.value)}
              className="w-full border border-gray-300 rounded-md px-3 py-2"
            >
              <option value="outside">Outside Room</option>
              <option value="inside">Inside Room</option>
            </select>
          </div>

          <div className="flex items-end">
            <div className="grid grid-cols-2 gap-2 w-full">
              <button
                onClick={() => handleScan('Fingerprint')}
                disabled={!selectedInstructor || !selectedRoom}
                className="flex items-center justify-center px-3 py-2 border border-transparent rounded-md text-sm font-medium text-white bg-green-600 hover:bg-green-700 disabled:bg-gray-400 disabled:cursor-not-allowed"
              >
                <FingerPrintIcon className="h-4 w-4 mr-1" />
                Fingerprint
              </button>
              <button
                onClick={() => handleScan('RFID')}
                disabled={!selectedInstructor || !selectedRoom}
                className="flex items-center justify-center px-3 py-2 border border-transparent rounded-md text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 disabled:bg-gray-400 disabled:cursor-not-allowed"
              >
                <div className="h-4 w-4 mr-1 bg-white rounded-sm"></div>
                RFID
              </button>
            </div>
          </div>
        </div>

        <div className="text-sm text-gray-600">
          <p><strong>Outside Room:</strong> Starts session + unlocks door OR ends session + locks door</p>
          <p><strong>Inside Room:</strong> Ends session only (no door control)</p>
        </div>
      </div>

      {/* Active Sessions - Moved to Top */}
      <div className="bg-white rounded-lg shadow p-6">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">Current Active Sessions</h2>

        {sessions.length === 0 ? (
          <p className="text-gray-500">No active sessions</p>
        ) : (
          <div className="space-y-4">
            {sessions.map((session) => (
              <div key={session.id} className="border border-gray-200 rounded-lg p-4">
                <div className="flex items-center justify-between mb-3">
                  <div className="flex items-center space-x-3">
                    <div className={`flex items-center space-x-2 px-3 py-1 rounded-full text-sm ${
                      session.status === 'active' 
                        ? 'bg-green-100 text-green-800' 
                        : 'bg-gray-100 text-gray-800'
                    }`}>
                      {session.status === 'active' ? (
                        <PlayIcon className="h-4 w-4" />
                      ) : (
                        <StopIcon className="h-4 w-4" />
                      )}
                      <span className="capitalize">{session.status}</span>
                    </div>

                    {session.door_unlocked_at && !session.door_locked_at && (
                      <div className="flex items-center space-x-1 px-2 py-1 bg-yellow-100 text-yellow-800 rounded-full text-xs">
                        <LockOpenIcon className="h-3 w-3" />
                        <span>Door Unlocked</span>
                      </div>
                    )}

                    {session.door_locked_at && (
                      <div className="flex items-center space-x-1 px-2 py-1 bg-red-100 text-red-800 rounded-full text-xs">
                        <LockClosedIcon className="h-3 w-3" />
                        <span>Door Locked</span>
                      </div>
                    )}
                  </div>

                  <div className="text-sm text-gray-500">
                    Duration: {getSessionDuration(session.start_time, session.end_time)}
                  </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                  <div>
                    <p className="text-sm font-medium text-gray-900">{session.course_code || session.subject_code}</p>
                    <p className="text-xs text-gray-600">{session.course_name || session.subject_name}</p>
                  </div>

                  <div>
                    <p className="text-sm font-medium text-gray-900">{session.instructor_name}</p>
                    <p className="text-xs text-gray-600">Instructor</p>
                  </div>

                  <div>
                    <p className="text-sm font-medium text-gray-900">{session.room_number}</p>
                    <p className="text-xs text-gray-600">{session.room_name}</p>
                  </div>

                  <div>
                    <p className="text-sm font-medium text-gray-900">
                      {formatTime(session.start_time)}
                      {session.end_time && ` - ${formatTime(session.end_time)}`}
                    </p>
                    <p className="text-xs text-gray-600">
                      {session.attendance_count || 0} students present
                    </p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Weekly Schedule */}
      <div className="bg-white rounded-lg shadow p-6">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">Weekly Schedule</h2>

        {allSchedules.length === 0 ? (
          <p className="text-gray-500">No schedules found</p>
        ) : (
          <div className="space-y-6">
            {groupSchedulesByDay(allSchedules).map((dayGroup) => {
              const currentDay = new Date().toLocaleDateString('en-US', { weekday: 'long' });
              const isToday = dayGroup.day === currentDay;

              return (
                <div key={dayGroup.day} className="border border-gray-200 rounded-lg overflow-hidden">
                  <div className={`px-4 py-3 ${isToday ? 'bg-blue-50 border-b border-blue-200' : 'bg-gray-50 border-b border-gray-200'}`}>
                    <h3 className={`text-lg font-semibold ${isToday ? 'text-blue-900' : 'text-gray-900'}`}>
                      {dayGroup.day}
                      {isToday && <span className="ml-2 text-sm font-normal text-blue-600">(Today)</span>}
                    </h3>
                  </div>
                  <div className="p-4">
                    {dayGroup.schedules.length === 0 ? (
                      <p className="text-gray-500 text-sm">No classes scheduled</p>
                    ) : (
                      <div className="space-y-3">
                        {dayGroup.schedules.map((schedule) => (
                          <div key={schedule.SCHEDULEID} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                            <div className="flex-1">
                              <h4 className="font-medium text-gray-900">{schedule.subject_code} - {schedule.subject_name}</h4>
                              <p className="text-sm text-gray-600">
                                {schedule.instructor_name} â€¢ {schedule.room_number} - {schedule.room_name}
                              </p>
                              <p className="text-xs text-gray-500">{schedule.ACADEMICYEAR} â€¢ {schedule.SEMESTER}</p>
                            </div>
                            <div className="text-right ml-4">
                              <p className="text-sm font-medium text-gray-900">
                                {formatScheduleTime(schedule.STARTTIME)} - {formatScheduleTime(schedule.ENDTIME)}
                              </p>
                              <p className="text-xs text-gray-500">Class Duration</p>
                            </div>
                          </div>
                        ))}
                      </div>
                    )}
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>

      {/* Scan Simulation Modal */}
      {showScanModal && (
        <div className="fixed inset-0 z-50 overflow-y-auto">
          <div className="flex items-center justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
            <div className="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity"></div>

            <div className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
              <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                <div className="flex items-center mb-4">
                  {scanType === 'fingerprint' ? (
                    <FingerPrintIcon className="h-8 w-8 text-blue-600 mr-3" />
                  ) : (
                    <IdentificationIcon className="h-8 w-8 text-green-600 mr-3" />
                  )}
                  <h3 className="text-lg font-medium text-gray-900">
                    Simulate {scanType === 'fingerprint' ? 'Fingerprint' : 'RFID'} Scan
                  </h3>
                </div>

                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700">Instructor</label>
                    <select
                      value={selectedInstructor}
                      onChange={(e) => setSelectedInstructor(e.target.value)}
                      className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                      required
                    >
                      <option value="">Select Instructor</option>
                      {instructors.map(instructor => (
                        <option key={instructor.USERID} value={instructor.USERID}>
                          {instructor.FIRSTNAME} {instructor.LASTNAME} ({instructor.EMPLOYEEID})
                        </option>
                      ))}
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700">Room</label>
                    <select
                      value={selectedRoom}
                      onChange={(e) => setSelectedRoom(e.target.value)}
                      className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                      required
                    >
                      <option value="">Select Room</option>
                      {rooms.map(room => (
                        <option key={room.ROOMID} value={room.ROOMID}>
                          {room.ROOMNUMBER} - {room.ROOMNAME}
                        </option>
                      ))}
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700">Scan Location</label>
                    <select
                      value={selectedLocation}
                      onChange={(e) => setSelectedLocation(e.target.value)}
                      className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                    >
                      <option value="outside">Outside Room</option>
                      <option value="inside">Inside Room</option>
                    </select>
                  </div>

                  <div className="bg-blue-50 p-4 rounded-md">
                    <div className="text-sm text-blue-800">
                      <strong>How it works:</strong>
                      <ul className="mt-2 list-disc list-inside space-y-1">
                        <li><strong>Outside Scan:</strong> Starts new session OR ends active session + controls door lock</li>
                        <li><strong>Inside Scan:</strong> Ends active session only (no door control)</li>
                        <li>Sessions are matched to today's schedule for the instructor and room</li>
                        <li>Door unlocks when session starts, locks when session ends</li>
                      </ul>
                    </div>
                  </div>
                </div>
              </div>

              <div className="bg-gray-50 px-4 py-3 sm:px-6 sm:flex sm:flex-row-reverse">
                <button
                  onClick={() => handleScan(scanType)}
                  className="w-full inline-flex justify-center rounded-md border border-transparent shadow-sm px-4 py-2 bg-blue-600 text-base font-medium text-white hover:bg-blue-700 focus:outline-none sm:ml-3 sm:w-auto sm:text-sm"
                >
                  Simulate Scan
                </button>
                <button
                  onClick={() => {
                    setShowScanModal(false);
                    setSelectedInstructor('');
                    setSelectedRoom('');
                    setSelectedLocation('outside');
                  }}
                  className="mt-3 w-full inline-flex justify-center rounded-md border border-gray-300 shadow-sm px-4 py-2 bg-white text-base font-medium text-gray-700 hover:bg-gray-50 focus:outline-none sm:mt-0 sm:ml-3 sm:w-auto sm:text-sm"
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Sessions;
