        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'multipart/form-data'
        }
      });

      setPreviewData(response.data);
      setShowPreview(true);
      toast.success(`Preview generated for ${response.data.summary.previewRows} users`);

    } catch (error) {
      /* eslint-disable */console.error(...oo_tx(`374784592_229_6_229_44_11`,'Preview error:', error));
      toast.error(error.response?.data?.message || 'Failed to preview Excel file');

      if (error.response?.data) {
        setPreviewData(error.response.data);
        setShowPreview(true);
      }
    } finally {
      setUploading(false);
    }
  };

  const handleConfirmUpload = async () => {
    if (!uploadFile) {
      toast.error('Please select a file to upload');
      return;
    }

    setUploading(true);
    const formData = new FormData();
    formData.append('excelFile', uploadFile);

    try {
