import React, { useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Layout = ({ children }) => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const location = useLocation();
  const navigate = useNavigate();
  const { user, logout } = useAuth();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const navigation = [
    { name: 'Dashboard', href: '/dashboard', icon: 'üìä' },
    { name: 'Users', href: '/users', icon: 'üë•' },
    { name: 'Courses', href: '/courses', icon: 'üìö' },
    { name: 'Rooms', href: '/rooms', icon: 'üèõÔ∏è' },
    { name: 'Schedules', href: '/schedules', icon: 'üìÖ' },
    { name: 'Sessions', href: '/sessions', icon: 'üéØ' },
    { name: 'Attendance Logs', href: '/attendance-logs', icon: 'üìù' },
    { name: 'Access Logs', href: '/access-logs', icon: 'üîê' },
  ];

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Sidebar */}
      <div className={`fixed inset-y-0 left-0 z-50 w-64 ldcu-gradient transform ${sidebarOpen ? 'translate-x-0' : '-translate-x-full'} transition-transform duration-300 ease-in-out lg:translate-x-0 lg:static lg:inset-0`}>
        <div className="flex items-center justify-center h-20 px-4 border-b border-white/10">
          <div className="flex items-center space-x-3">
            <div className="w-12 h-12 bg-white rounded-full flex items-center justify-center">
              <img 
                src="/ldcu-logo.png" 
                alt="LDCU" 
                className="w-10 h-10 object-contain"
                onError={(e) => {
                  e.target.style.display = 'none';
                  e.target.nextSibling.style.display = 'block';
                }}
              />
              <span 
                className="text-2xl font-bold text-red-800 hidden"
                style={{ color: 'var(--ldcu-maroon)' }}
              >
                LDCU
              </span>
            </div>
            <div className="text-white">
              <h1 className="text-xl font-bold">LDCU</h1>
              <p className="text-sm text-white/80">Attendance System</p>
            </div>
          </div>
        </div>

        <nav className="mt-8 px-4 space-y-2">
          {navigation.map((item) => {
            const isActive = location.pathname === item.href;
            return (
              <Link
                key={item.name}
                to={item.href}
                className={`ldcu-nav-item ${isActive ? 'active' : ''}`}
              >
                <span className="text-xl">{item.icon}</span>
                <span className="font-medium">{item.name}</span>
              </Link>
            );
          })}
        </nav>

        <div className="absolute bottom-4 left-4 right-4">
          <div className="bg-white/10 rounded-lg p-4 text-white">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-white/20 rounded-full flex items-center justify-center">
                <span className="text-lg">üë§</span>
              </div>
              <div className="flex-1 min-w-0">
                <p className="font-medium truncate">{user?.name || 'Admin User'}</p>
                <p className="text-sm text-white/80 truncate">{user?.email || user?.username}</p>
              </div>
            </div>
            <button
              onClick={handleLogout}
              className="mt-3 w-full ldcu-btn-secondary text-sm"
            >
              Sign Out
            </button>
          </div>
        </div>
      </div>

      {/* Mobile sidebar overlay */}
      {sidebarOpen && (
        <div 
          className="fixed inset-0 z-40 bg-black bg-opacity-50 lg:hidden"
          onClick={() => setSidebarOpen(false)}
        />
      )}

      {/* Main content */}
      <div className="lg:pl-64">
        {/* Top header */}
        <header className="bg-white shadow-sm border-b-2" style={{ borderBottomColor: 'var(--ldcu-gold)' }}>
          <div className="flex items-center justify-between h-16 px-4 sm:px-6 lg:px-8">
            <button
              onClick={() => setSidebarOpen(!sidebarOpen)}
              className="lg:hidden p-2 rounded-md text-gray-600 hover:text-gray-900 hover:bg-gray-100"
            >
              <span className="sr-only">Open sidebar</span>
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
              </svg>
            </button>

            <div className="flex-1">
              <h1 className="text-2xl font-bold" style={{ color: 'var(--ldcu-maroon)' }}>
                {navigation.find(item => item.href === location.pathname)?.name || 'LDCU Attendance System'}
              </h1>
            </div>

            <div className="flex items-center space-x-4">
              <div className="text-sm" style={{ color: 'var(--ldcu-maroon)' }}>
                <span className="font-medium">Liceo de Cagayan University</span>
              </div>
              <div className="w-8 h-8 rounded-full flex items-center justify-center" style={{ backgroundColor: 'var(--ldcu-maroon)' }}>
                <span className="text-white text-sm font-bold">
                  {(user?.name || 'A').charAt(0).toUpperCase()}
                </span>
              </div>
            </div>
          </div>
        </header>

        {/* Page content */}
        <main className="p-4 sm:p-6 lg:p-8">
          <div className="ldcu-fade-in">
            {children}
          </div>
        </main>
      </div>
    </div>
  );
};

export default Layout;
