    if (!data) return [];

    switch (sortBy) {
      case 'rooms':
        return data.rooms.data || [];
      case 'subjects':
        return data.subjects.data || [];
      case 'schedules':
        // Group schedules by subject for unified display
        const schedules = data.schedules.data || [];
        return groupSchedulesBySubject(schedules);
      default:
